import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";
import { ChevronLeft, ChevronRight, Calendar } from 'lucide-react';

// Event Calendar Component
function EventCalendar({ events = [], onDateClick }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);

  const eventDates = events.map(event => {
    const date = new Date(event.date);
    return date.toISOString().split('T')[0];
  });

  const getEventsForDate = (dateStr) => {
    return events.filter(event => {
      const eventDate = new Date(event.date).toISOString().split('T')[0];
      return eventDate === dateStr;
    });
  };

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const daysInMonth = lastDay.getDate();
  const startingDayOfWeek = firstDay.getDay();

  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const dayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  const previousMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
    setSelectedDate(null);
  };

  const nextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
    setSelectedDate(null);
  };

  const goToToday = () => {
    setCurrentDate(new Date());
    setSelectedDate(null);
  };

  const handleDateClick = (day) => {
    const dateStr = new Date(year, month, day).toISOString().split('T')[0];
    setSelectedDate(dateStr);
    if (onDateClick) {
      onDateClick(dateStr);
    }
  };

  const calendarDays = [];
  
  for (let i = 0; i < startingDayOfWeek; i++) {
    calendarDays.push(<div key={`empty-${i}`} className="aspect-square" />);
  }

  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = new Date(year, month, day).toISOString().split('T')[0];
    const hasEvent = eventDates.includes(dateStr);
    const isToday = new Date().toISOString().split('T')[0] === dateStr;
    const isSelected = selectedDate === dateStr;
    const dayEvents = getEventsForDate(dateStr);

    calendarDays.push(
      <button
        key={day}
        onClick={() => handleDateClick(day)}
        className={`
          aspect-square p-2 rounded-lg text-sm font-medium transition-all
          hover:scale-105 hover:shadow-lg relative
          ${isToday ? 'ring-2 ring-blue-500' : ''}
          ${isSelected ? 'bg-blue-600 text-white' : 'bg-gray-800/50 text-gray-300'}
          ${hasEvent && !isSelected ? 'bg-green-600/30 border-2 border-green-500' : ''}
          ${!hasEvent && !isSelected ? 'hover:bg-gray-700/50' : ''}
        `}
      >
        <span className="relative z-10">{day}</span>
        {hasEvent && (
          <div className="absolute bottom-1 left-1/2 transform -translate-x-1/2 flex gap-0.5">
            {dayEvents.slice(0, 3).map((_, idx) => (
              <div key={idx} className="w-1 h-1 rounded-full bg-green-400" />
            ))}
          </div>
        )}
      </button>
    );
  }

  const selectedDayEvents = selectedDate ? getEventsForDate(selectedDate) : [];

  return (
    <div className="bg-gradient-to-br from-gray-800/90 to-gray-900/90 backdrop-blur-md border border-gray-700/50 rounded-2xl shadow-2xl p-6">
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="bg-blue-600/20 p-2 rounded-lg">
            <Calendar className="w-6 h-6 text-blue-400" />
          </div>
          <div>
            <h3 className="text-xl font-bold text-white">Event Calendar</h3>
            <p className="text-xs text-gray-400">Click dates to view events</p>
          </div>
        </div>
        <button
          onClick={goToToday}
          className="bg-blue-600/20 hover:bg-blue-600/30 text-blue-400 px-3 py-1.5 rounded-lg text-xs font-medium transition"
        >
          Today
        </button>
      </div>

      <div className="flex items-center justify-between mb-6">
        <button
          onClick={previousMonth}
          className="p-2 hover:bg-gray-700/50 rounded-lg transition"
        >
          <ChevronLeft className="w-5 h-5 text-gray-400" />
        </button>
        <h4 className="text-lg font-semibold text-white">
          {monthNames[month]} {year}
        </h4>
        <button
          onClick={nextMonth}
          className="p-2 hover:bg-gray-700/50 rounded-lg transition"
        >
          <ChevronRight className="w-5 h-5 text-gray-400" />
        </button>
      </div>

      <div className="grid grid-cols-7 gap-2 mb-2">
        {dayNames.map(day => (
          <div
            key={day}
            className="text-center text-xs font-semibold text-gray-500 uppercase"
          >
            {day}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-2 mb-4">
        {calendarDays}
      </div>

      <div className="flex items-center justify-center gap-6 pt-4 border-t border-gray-700/50 text-xs">
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded bg-green-600/30 border-2 border-green-500" />
          <span className="text-gray-400">Has Events</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded ring-2 ring-blue-500" />
          <span className="text-gray-400">Today</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-3 h-3 rounded bg-blue-600" />
          <span className="text-gray-400">Selected</span>
        </div>
      </div>

      {selectedDate && (
        <div className="mt-6 pt-6 border-t border-gray-700/50">
          <h4 className="text-sm font-semibold text-gray-400 mb-3">
            Events on {new Date(selectedDate + 'T00:00:00').toLocaleDateString('en-US', { 
              month: 'long', 
              day: 'numeric',
              year: 'numeric'
            })}
          </h4>
          {selectedDayEvents.length === 0 ? (
            <p className="text-xs text-gray-500 italic">No events scheduled</p>
          ) : (
            <div className="space-y-2">
              {selectedDayEvents.map((event) => (
                <div
                  key={event._id}
                  className="bg-gray-700/30 border border-green-500/30 p-3 rounded-lg hover:border-green-500/50 transition"
                >
                  <p className="text-sm font-medium text-white mb-1">{event.name}</p>
                  <p className="text-xs text-gray-400"> {event.venue}</p>
                  {event.strength && (
                    <p className="text-xs text-gray-400 mt-1"> {event.strength} participants</p>
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

function OrganizerDashboard() {
  const [eventsData, setEventsData] = useState([]);
  const [search, setSearch] = useState("");
  const [date, setDate] = useState("");
  const [editEvent, setEditEvent] = useState(null);

  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [pastEvents, setPastEvents] = useState([]);
  const [viewStates, setViewStates] = useState({});

  const [deleteModal, setDeleteModal] = useState({ open: false, eventId: null });

  useEffect(() => {
    getAllEvents();
  }, []);

  const getAllEvents = () => {
    axios
      .get(base_url + "events")
      .then((res) => {
        const allEvents = res.data.events;
        setEventsData(allEvents);

        const today = new Date();
        const upcoming = allEvents.filter((event) => new Date(event.date) >= today);
        const past = allEvents.filter((event) => new Date(event.date) < today);

        upcoming.sort((a, b) => new Date(a.date) - new Date(b.date));
        past.sort((a, b) => new Date(b.date) - new Date(a.date));

        setUpcomingEvents(upcoming);
        setPastEvents(past);
      })
      .catch((err) => console.log(err.message));
  };

  const [name, setName] = useState("");
  const [eventDate, setEventDate] = useState("");
  const [venue, setVenue] = useState("");
  const [strength, setStrength] = useState("");
  const [shortDesc, setShortDesc] = useState("");
  const [about, setAbout] = useState("");
  const [learning, setLearning] = useState("");

  const handleLogout = () => {
    window.location.href = "/home";
  };

  const confirmDelete = (id) => {
    setDeleteModal({ open: true, eventId: id });
  };

  const handleDeleteConfirmed = () => {
    axios.delete(base_url + `events/${deleteModal.eventId}`)
      .then(() => {
        setEventsData(eventsData.filter(ev => ev._id !== deleteModal.eventId));
        getAllEvents();
        setDeleteModal({ open: false, eventId: null });
      })
      .catch(err => console.log(err));
  };

  const handleDeleteCancel = () => {
    setDeleteModal({ open: false, eventId: null });
  };

  const handleSaveEvent = async (e) => {
    e.preventDefault();
    if (!name || !eventDate || !venue || !strength || !shortDesc || !about || !learning) {
      alert("Please fill all fields");
      return;
    }

    const eventData = {
      name,
      date: eventDate,
      venue,
      strength: parseInt(strength),
      shortDesc,
      about,
      learning,
    };

    if (editEvent) {
      axios
        .put(base_url + `events/${editEvent._id}`, eventData)
        .then(() => {
          alert("Event updated successfully!");
          setEditEvent(null);
          setName("");
          setEventDate("");
          setVenue("");
          setStrength("");
          setShortDesc("");
          setAbout("");
          setLearning("");
          getAllEvents();
        })
        .catch((err) => console.log(err));
    } else {
      axios
        .post(base_url + "events", eventData)
        .then(() => {
          alert("Event created successfully!");
          setName("");
          setEventDate("");
          setVenue("");
          setStrength("");
          setShortDesc("");
          setAbout("");
          setLearning("");
          getAllEvents();
        })
        .catch((err) => console.log(err));
    }
  };

  const handleEditClick = (event) => {
    setEditEvent(event);
    setName(event.name);
    setEventDate(event.date);
    setVenue(event.venue);
    setStrength(event.strength);
    setShortDesc(event.shortDesc);
    setAbout(event.about);
    setLearning(event.learning);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-8 py-4 flex justify-between items-center shadow-lg border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">Event Coordination Engine</h1>
        <div className="flex space-x-8 text-lg">
          <a href="/home" className="hover:text-blue-300 transition">Home</a>
          <a href="#browse" className="hover:text-blue-300 transition">Browse Events</a>
          <a href="#profile" className="hover:text-blue-300 transition">Profile</a>
          <button onClick={handleLogout} className="hover:text-blue-400 transition">Logout</button>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="text-center py-12">
        <h2 className="text-4xl font-bold text-blue-400 tracking-wide">
          Welcome, <span className="text-white">Organizer</span>
        </h2>
        <p className="text-gray-400 mt-3 text-lg">Manage all users and events</p>
        <button
          onClick={() => document.getElementById("browse").scrollIntoView({ behavior: "smooth" })}
          className="mt-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl font-semibold shadow-lg transition"
        >
          Browse Events
        </button>
      </section>

      <div className="flex flex-col md:flex-row px-10 gap-10 pb-12">
        {/* Main Content */}
        <div className="flex-1">
          {/* Event Form */}
          <div className="mb-6 bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-2xl font-bold text-blue-400 mb-4">
              {editEvent ? "Edit Event" : "Create Event"}
            </h3>
            <form onSubmit={handleSaveEvent} className="space-y-4">
              <input type="text" placeholder="Event Name" value={name} onChange={e => setName(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white" />
              <input type="date" value={eventDate} onChange={e => setEventDate(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white" />
              <input type="text" placeholder="Venue" value={venue} onChange={e => setVenue(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white" />
              <input type="number" placeholder="Strength" value={strength} onChange={e => setStrength(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white" />
              <textarea placeholder="Short Description" value={shortDesc} onChange={e => setShortDesc(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-20 resize-none" />
              <textarea placeholder="About Event" value={about} onChange={e => setAbout(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-24 resize-none" />
              <textarea placeholder="Learning Outcomes" value={learning} onChange={e => setLearning(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-24 resize-none" />
              <div className="flex justify-end gap-4 mt-2">
                {editEvent && <button type="button" onClick={() => setEditEvent(null)} className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded">Cancel</button>}
                <button type="submit" className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded">{editEvent ? "Update" : "Save"}</button>
              </div>
            </form>
          </div>

          {/* Events List */}
          <div className="flex flex-col gap-10">
            {/* Upcoming Events */}
            <section id="browse" className="mb-12">
              <h3 className="text-2xl font-bold mb-6 text-blue-400">Upcoming Events</h3>
              {upcomingEvents.length === 0 ? (
                <p className="text-gray-400">No upcoming events right now.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {upcomingEvents.map((event) => (
                    <div key={event._id} className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-400/20 transition-all">
                      <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                      <p className="text-gray-400 text-sm mb-1"> {event.date}</p>
                      <p className="text-gray-400 text-sm mb-1"> {event.venue}</p>
                      <p className="text-gray-400 text-sm mb-1"> {event.shortDesc}</p>
                      <div className="flex gap-2 mt-3">
                        <button onClick={() => handleEditClick(event)} className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded transition">Edit</button>
                        <button onClick={() => confirmDelete(event._id)} className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded transition">Delete</button>
                        <button onClick={() => setViewStates((prev) => ({ ...prev, [event._id]: !prev[event._id] }))} className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded transition">
                          {viewStates[event._id] ? "Hide Details" : "View"}
                        </button>
                      </div>
                      {viewStates[event._id] && (
                        <div className="mt-4 bg-gray-700/50 p-3 rounded-lg text-gray-300 text-sm space-y-2">
                          <p><strong>About Event:</strong> {event.about}</p>
                          <p><strong>Learning Outcomes:</strong> {event.learning}</p>
                          <p><strong>Full Description:</strong> {event.shortDesc}</p>
                          <p><strong>Strength:</strong> {event.strength}</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </section>

            {/* Past Events */}
            <section id="past-events" className="mb-12">
              <h3 className="text-2xl font-bold mb-6 text-blue-400">Past Events</h3>
              {pastEvents.length === 0 ? (
                <p className="text-gray-400">No past events yet.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {pastEvents.map((event) => (
                    <div key={event._id} className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-gray-500/30 transition-all">
                      <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                      <p className="text-gray-400 text-sm mb-1"> {event.date}</p>
                      <p className="text-gray-400 text-sm mb-1"> {event.venue}</p>
                      <p className="text-gray-400 text-sm mb-1"> Strength: {event.strength}</p>
                      <p className="text-gray-400 text-sm mb-1"> {event.shortDesc}</p>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </div>
        </div>

        {/* Sidebar */}
        <div className="w-full md:w-1/3 space-y-8">
          {/* Calendar */}
          <EventCalendar 
            events={eventsData}
            onDateClick={(date) => setDate(date)}
          />

          {/* Search & Filter */}
          <div className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Search & Filter</h3>
            <input type="text" placeholder="Search by keyword..." className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none" value={search} onChange={(e) => setSearch(e.target.value)} />
            <input type="date" className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none" value={date} onChange={(e) => setDate(e.target.value)} />
            <button onClick={() => { setSearch(""); setDate(""); }} className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">Clear Filters</button>
          </div>

          {/* Profile Section */}
          <div id="profile" className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Profile</h3>
            <div className="flex items-center space-x-4 mb-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center font-bold text-lg">O</div>
              <div>
                <p className="font-semibold text-white">Organizer Name</p>
                <p className="text-gray-400 text-sm">organizer@email.com</p>
              </div>
            </div>
            <button className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">Edit Profile</button>
          </div>
        </div>
      </div>

      {/* Delete Confirmation Modal */}
      {deleteModal.open && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-2xl shadow-lg w-80 text-white">
            <h3 className="text-xl font-bold mb-4 text-red-500">Confirm Delete</h3>
            <p className="mb-6">Are you sure you want to delete this event?</p>
            <div className="flex justify-end gap-4">
              <button onClick={handleDeleteCancel} className="bg-gray-600 hover:bg-gray-700 px-4 py-2 rounded">Cancel</button>
              <button onClick={handleDeleteConfirmed} className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded">Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default OrganizerDashboard;
