import { useState } from "react";
import { motion } from "framer-motion";
import Navbar from "../components/Navbar";
import Hero from "../components/Hero";

const events = [
  {
    id: 1,
    title: "Hackathon 2025, AI Workshop, National Seminar on Data Science.",
    date: "20‚Äì21 Oct 2025, 9:00 AM",
    image: "/hack.jpg",
    venue: "Computer Science Lab, Block A",
    description: "A 24-hour coding challenge where students collaborate to build innovative solutions for real-world problems."
  },
  {
    id: 2,
    title: "Cybersecurity Workshop 2025, Blockchain Seminar, Cloud Bootcamp",
    date: "5‚Äì6 Nov 2025, 10:00 AM",
    image: "/hacky.jpg",
    venue: "Information Technology Lab, Block B",
    description: "An interactive workshop covering the latest trends in cybersecurity, blockchain technology, and cloud computing, with hands-on sessions for participants."
  },
  {
    id: 3,
    title: "Pre-Placement Talk 2025 ‚Äì Career Guidance & Industry Insights",
    date: "15 Nov 2025, 11:00 AM",
    image: "/pre.jpg",
    venue: "Auditorium, Main Block",
    description: "An informative session where industry experts provide insights into career opportunities, placement strategies, and tips to excel in interviews and group discussions."
  },
  {
    id: 4,
    title: "Ideathon Competitions 2025 ‚Äì Innovative Ideas Challenge",
    date: "25‚Äì26 Nov 2025, 10:00 AM",
    image: "/hacky.jpg",
    venue: "Innovation Lab, Block C",
    description: "A 2-day competition encouraging students to brainstorm, develop, and pitch creative solutions to real-world problems, fostering innovation and teamwork."
  }
];

const animationImages = [
  "/hack.jpg",
  "/hacky.jpg",
  "/pre.jpg",
  "/hero-bg.jpg"
];

function Home() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedEvent, setSelectedEvent] = useState(null);

  const filteredEvents = events.filter(event =>
    event.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      <Navbar onSearch={setSearchQuery} />
      <Hero />

      {/* üåü Animated Carousel Section */}
      <section className="my-16 relative overflow-hidden">
        <h2 className="text-center text-3xl font-bold text-blue-400 mb-6">
          Upcoming Highlights
        </h2>
        <div className="relative flex justify-center items-center">
          <motion.div
            className="flex gap-6"
            animate={{
              x: ["0%", "-100%"]
            }}
            transition={{
              repeat: Infinity,
              duration: 25,
              ease: "linear"
            }}
          >
            {[...animationImages, ...animationImages].map((src, i) => (
              <motion.img
                key={i}
                src={src}
                alt="Event slide"
                className="w-[400px] h-[250px] object-cover rounded-2xl shadow-lg border border-gray-700"
                whileHover={{ scale: 1.05 }}
              />
            ))}
          </motion.div>
        </div>
      </section>

      {/* üéâ Promoted Events Section */}
      <section className="px-8 py-12">
        <h2 className="text-3xl font-bold mb-8 text-blue-400">Promoted Events</h2>
        {filteredEvents.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
            {filteredEvents.map(event => (
              <div
                key={event.id}
                className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-500/30 hover:-translate-y-1 transition-all"
              >
                <img
                  src={event.image}
                  alt={event.title}
                  className="w-full h-40 object-cover rounded-xl mb-4"
                />
                <h3 className="text-lg font-semibold text-white mb-2">{event.title}</h3>
                <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                <p className="text-gray-400 text-sm mb-4">{event.description}</p>
                <button
                  onClick={() => setSelectedEvent(event)}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-xl font-semibold transition"
                >
                  View Details
                </button>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-400">No events found.</p>
        )}
      </section>

      {/* ü™© Event Modal */}
      {selectedEvent && (
        <EventModal
          event={selectedEvent}
          onClose={() => setSelectedEvent(null)}
        />
      )}
    </div>
  );
}

// üåü Event Modal Component
function EventModal({ event, onClose }) {
  const handleRegister = () => {
    window.location.href = "/login";
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
      <div className="bg-gray-900 p-8 rounded-2xl max-w-lg w-full space-y-4 relative">
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-white text-xl font-bold hover:text-red-500"
        >
          &times;
        </button>
        <img
          src={event.image}
          alt={event.title}
          className="w-full h-48 object-cover rounded-xl mb-4"
        />
        <h3 className="text-2xl font-bold text-blue-400">{event.title}</h3>
        <p className="text-gray-400">üìÖ {event.date}</p>
        <p className="text-gray-400">üìç {event.venue}</p>
        <p className="text-gray-400 mt-2">{event.description}</p>
        <div className="flex gap-4 mt-4">
          <button
            onClick={handleRegister}
            className="flex-1 bg-green-600 hover:bg-green-700 text-white py-2 rounded-xl font-semibold transition"
          >
            Register
          </button>
          <button
            onClick={onClose}
            className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-2 rounded-xl font-semibold transition"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
