import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

/* ================= CALENDAR COMPONENT (UNCHANGED) ================= */
function CompactEventCalendar({ events = [] }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showEventDetails, setShowEventDetails] = useState(false);

  const eventDates = events.map(event => event.date.split("T")[0]);

  const getEventsForDate = (dateStr) =>
    events.filter(event => event.date.split("T")[0] === dateStr);

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const startingDayOfWeek = new Date(year, month, 1).getDay();

  const monthNames = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
  const dayNames = ["S","M","T","W","T","F","S"];

  const handleDateClick = (day) => {
    const m = String(month + 1).padStart(2,"0");
    const d = String(day).padStart(2,"0");
    setSelectedDate(`${year}-${m}-${d}`);
    setShowEventDetails(true);
  };

  return (
    <div className="bg-gray-800/90 border border-gray-700 rounded-xl shadow-lg p-3 w-64">
      <h4 className="text-center text-white text-sm font-semibold mb-2">
        {monthNames[month]} {year}
      </h4>

      <div className="grid grid-cols-7 gap-1 text-xs">
        {dayNames.map(d => <div key={d} className="text-center text-gray-400">{d}</div>)}
        {[...Array(startingDayOfWeek)].map((_,i)=><div key={i}></div>)}
        {[...Array(daysInMonth)].map((_,i)=>(
          <button
            key={i}
            onClick={()=>handleDateClick(i+1)}
            className={`p-1 rounded ${
              eventDates.includes(`${year}-${String(month+1).padStart(2,"0")}-${String(i+1).padStart(2,"0")}`)
              ? "bg-green-500 text-white"
              : "text-gray-400"
            }`}
          >
            {i+1}
          </button>
        ))}
      </div>

      {showEventDetails && selectedDate && (
        <div className="mt-3 text-xs text-gray-300">
          <b>Events:</b>
          {getEventsForDate(selectedDate).map(e=>(
            <div key={e._id}>{e.name}</div>
          ))}
        </div>
      )}
    </div>
  );
}

/* ================= USER DASHBOARD ================= */
function UserDashboard() {
  const [eventsData, setEventsData] = useState([]);
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [pastEvents, setPastEvents] = useState([]);

  const [searchName, setSearchName] = useState("");
  const [searchVenue, setSearchVenue] = useState("");
  const [date, setDate] = useState("");

  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [confirmEvent, setConfirmEvent] = useState(null);

  useEffect(() => {
    axios.get(base_url + "events").then(res => {
      const all = res.data.events;
      setEventsData(all);

      const today = new Date();
      setUpcomingEvents(all.filter(e => new Date(e.date) >= today));
      setPastEvents(all.filter(e => new Date(e.date) < today));
    });
  }, []);

  /* ---------- FILTER (DATE / NAME / VENUE) ---------- */
  const filteredUpcomingEvents = upcomingEvents.filter(event => {
    return (
      (!date || event.date === date) &&
      event.name.toLowerCase().includes(searchName.toLowerCase()) &&
      event.venue.toLowerCase().includes(searchVenue.toLowerCase())
    );
  });

  /* ---------- REGISTER WITH CONFIRMATION ---------- */
  const handleConfirmRegister = () => {
    if (!registeredEvents.find(e => e._id === confirmEvent._id)) {
      setRegisteredEvents([...registeredEvents, confirmEvent]);
    }
    setConfirmEvent(null);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">

      {/* ================= NAVBAR ================= */}
      <nav className="bg-black px-8 py-4 flex justify-between items-center">
        <h1 className="text-xl font-bold text-blue-400">Event Coordination Engine</h1>
        <div className="flex gap-6">
          <a href="#browse">Browse Events</a>
          <a href="#registered">Registered Events</a>
        </div>
      </nav>

      {/* ================= CALENDAR FIXED TOP-LEFT ================= */}
      <div className="fixed top-24 left-6 z-40">
        <CompactEventCalendar events={eventsData} />
      </div>

      {/* ================= FILTERS ================= */}
      <div className="px-10 mt-6 flex gap-4">
        <input
          placeholder="Search by name"
          value={searchName}
          onChange={e=>setSearchName(e.target.value)}
          className="bg-gray-800 p-2 rounded"
        />
        <input
          placeholder="Search by venue"
          value={searchVenue}
          onChange={e=>setSearchVenue(e.target.value)}
          className="bg-gray-800 p-2 rounded"
        />
        <input
          type="date"
          value={date}
          onChange={e=>setDate(e.target.value)}
          className="bg-gray-800 p-2 rounded"
        />
      </div>

      {/* ================= UPCOMING EVENTS ================= */}
      <section id="browse" className="px-10 mt-8">
        <h2 className="text-xl text-blue-400 mb-4">Upcoming Events</h2>
        <div className="grid grid-cols-3 gap-6">
          {filteredUpcomingEvents.map(event=>(
            <div key={event._id} className="bg-gray-800 p-4 rounded">
              <h3 className="font-semibold">{event.name}</h3>
              <p>{event.date}</p>
              <p>{event.venue}</p>
              <button
                onClick={()=>setConfirmEvent(event)}
                className="bg-blue-600 px-3 py-1 mt-2 rounded"
              >
                Register
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* ================= REGISTERED EVENTS ================= */}
      <section id="registered" className="px-10 mt-12">
        <h2 className="text-xl text-blue-400 mb-4">Registered Events</h2>
        {registeredEvents.map(e=>(
          <div key={e._id} className="bg-gray-800 p-3 mb-2 rounded">
            {e.name} â€“ {e.date}
          </div>
        ))}
      </section>

      {/* ================= CONFIRMATION MODAL ================= */}
      {confirmEvent && (
        <div className="fixed inset-0 bg-black/70 flex justify-center items-center">
          <div className="bg-gray-800 p-6 rounded w-96">
            <h3 className="text-lg text-blue-400 mb-3">Confirm Registration</h3>
            <p><b>Name:</b> {confirmEvent.name}</p>
            <p><b>Date:</b> {confirmEvent.date}</p>
            <p><b>Venue:</b> {confirmEvent.venue}</p>

            <div className="flex justify-end gap-4 mt-4">
              <button onClick={()=>setConfirmEvent(null)} className="bg-gray-600 px-3 py-1 rounded">Cancel</button>
              <button onClick={handleConfirmRegister} className="bg-green-600 px-3 py-1 rounded">Confirm</button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

export default UserDashboard;
