import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Register() {
  const [fullName, setFullName] = useState("");
  const [rollNumber, setRollNumber] = useState("");
  const [branch, setBranch] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("student"); // default role
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();
    localStorage.setItem(
      "user",
      JSON.stringify({ fullName, rollNumber, branch, email, password, role })
    );
    alert("Registration successful!");
    navigate("/");
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-red-600 via-red-500 to-red-700">
      <form
        onSubmit={handleRegister}
        className="bg-white shadow-2xl rounded-2xl p-8 w-96 transform transition-all duration-500 hover:scale-105"
      >
        <h2 className="text-3xl font-bold mb-6 text-center text-red-600">
          Register
        </h2>

        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
          required
        />

        <input
          type="text"
          placeholder="Roll Number"
          value={rollNumber}
          onChange={(e) => setRollNumber(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
          required
        />

        <input
          type="text"
          placeholder="Branch"
          value={branch}
          onChange={(e) => setBranch(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
          required
        />

        <input
          type="email"
          placeholder="College Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
          required
        />

        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-red-400 outline-none"
        >
          <option value="student">Student</option>
          <option value="organizer">Organizer</option>
          <option value="admin">Admin</option>
        </select>

        <button
          type="submit"
          className="bg-red-600 text-white w-full py-2 rounded-lg hover:bg-red-700 transition"
        >
          Register
        </button>

        <p className="mt-4 text-center text-gray-600">
          Already have an account?{" "}
          <a href="/" className="text-red-600 font-semibold hover:underline">
            Login
          </a>
        </p>
      </form>
    </div>
  );
}

export default Register;
