import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import EventCard from "../components/EventCard";

const events = [
  { id: 1, title: "Hackathon", price: "9.00", image: "/hack.jpg" },
  { id: 2, title: "Blockchain Conference", price: "15.00", image: "/event2.jpg" },
  { id: 3, title: "Music Fest", price: "12.00", image: "/event3.jpg" },
  { id: 4, title: "Art & Culture Expo", price: "10.00", image: "/event4.jpg" },
];

function Home() {
  return (
    <div>
      <Navbar />
      <Hero />
      <section className="px-8 py-12">
        <h2 className="text-2xl font-bold mb-6">Promoted events</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          {events.map(event => (
            <EventCard key={event.id} {...event} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default Home;
