import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

function UserDashboard() {
  const [eventsData, setEventsData] = useState("")
  useEffect(() => {
    getAllEvents()
  }, [])

  const getAllEvents = () => {
    axios.get(base_url + "events")
      .then((res) => {
        setEventData(res.data)
      })
      .catch((err) => {
        console.log(err.message)
      })
  }
  // const eventsData = [
  //   { id: 1, title: "Tech Talk 2025", date: "2025-10-25", category: "Technology", venue: "Online" },
  //   { id: 2, title: "Art Exhibition", date: "2025-11-10", category: "Culture", venue: "City Art Gallery" },
  //   { id: 3, title: "Music Concert", date: "2025-12-05", category: "Entertainment", venue: "Central Park" },
  //   { id: 4, title: "AI Workshop", date: "2025-11-20", category: "Technology", venue: "Innovation Lab" },
  //   // Sample past events
  //   { id: 5, title: "Hackathon 2025", date: "2025-09-15", category: "Technology", venue: "Tech Hub" },
  //   { id: 6, title: "Classical Music Night", date: "2025-08-30", category: "Entertainment", venue: "Grand Hall" },

  // ];

  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [recentlyRegistered, setRecentlyRegistered] = useState(null);

  const filteredEvents = eventsData.filter((event) => {
    return (
      event.title.toLowerCase().includes(search.toLowerCase()) &&
      (category ? event.category === category : true) &&
      (date ? event.date === date : true)
    );
  });

  const handleRegister = (event) => {
    if (!registeredEvents.find((e) => e.id === event.id)) {
      setRegisteredEvents([...registeredEvents, event]);
      setRecentlyRegistered(event);
    } else {
      setRecentlyRegistered({ ...event, already: true });
    }
  };

  const handleLogout = () => {
    window.location.href = "/home";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-8 py-4 flex justify-between items-center shadow-lg border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">Event Coordination Engine</h1>
        <div className="flex space-x-8 text-lg">
          <a href="/home" className="hover:text-blue-300 transition">Home</a>
          <a href="#browse" className="hover:text-blue-300 transition">Browse Events</a>
          <a href="#registered" className="hover:text-blue-300 transition">My Events</a>
          <a href="#profile" className="hover:text-blue-300 transition">Profile</a>
          <button onClick={handleLogout} className="hover:text-blue-400 transition">Logout</button>
        </div>
      </nav>

      {/* Selected Event Details (Top) */}
      {selectedEvent && (
        <div className="bg-gray-800/90 border border-gray-700 p-6 rounded-2xl shadow-lg mx-10 my-4">
          <h3 className="text-2xl font-bold mb-4 text-blue-400">Event Details</h3>
          <p className="text-white font-semibold mb-1">Title: {selectedEvent.title}</p>
          <p className="text-gray-400 mb-1">Date: {selectedEvent.date}</p>
          <p className="text-gray-400 mb-1">Venue: {selectedEvent.venue}</p>
          <p className="text-gray-400 mb-3">Category: {selectedEvent.category}</p>
          <button
            onClick={() => setSelectedEvent(null)}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded transition"
          >
            Close Details
          </button>
        </div>
      )}

      {/* Registration Notification (Top) */}
      {recentlyRegistered && (
        <div className={`bg-gray-800/90 border ${recentlyRegistered.already ? "border-yellow-500" : "border-green-500"
          } p-6 rounded-2xl shadow-lg mx-10 my-4`}>
          <p className={`text-lg font-semibold mb-2 ${recentlyRegistered.already ? "text-yellow-400" : "text-green-400"
            }`}>
            {recentlyRegistered.already
              ? `‚ö†Ô∏è You already registered for ${recentlyRegistered.title}`
              : `‚úÖ Successfully registered for ${recentlyRegistered.title}`}
          </p>
          <button
            onClick={() => setRecentlyRegistered(null)}
            className="bg-red-600 hover:bg-red-700 text-white py-1 px-3 rounded transition"
          >
            Close
          </button>
        </div>
      )}

      {/* Hero Section */}
      <section className="text-center py-12">
        <h2 className="text-4xl font-bold text-blue-400 tracking-wide">
          Welcome, <span className="text-white">User</span>
        </h2>
        <p className="text-gray-400 mt-3 text-lg">
          Discover and register for exciting upcoming events
        </p>
        <button
          onClick={() =>
            document.getElementById("browse").scrollIntoView({ behavior: "smooth" })
          }
          className="mt-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl font-semibold shadow-lg transition"
        >
          Browse Events
        </button>
      </section>

      <div className="flex flex-col md:flex-row px-10 gap-10 pb-12">
        {/* Main Content */}
        <div className="flex-1">
          {/* Upcoming Events */}
          <section id="browse" className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-blue-400">Upcoming Events</h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredEvents.map((event) => (
                <div
                  key={event.id}
                  className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-500/30 hover:-translate-y-1 transition-all"
                >
                  <h4 className="text-lg font-semibold text-white mb-2">{event.title}</h4>
                  <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                  <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                  <p className="text-gray-400 text-sm mb-4">üè∑Ô∏è {event.category}</p>

                  {/* Buttons: Register + View Details */}
                  <div className="flex gap-2">
                    <button
                      onClick={() => handleRegister(event)}
                      className={`flex-1 py-2 rounded-lg font-semibold transition-all ${registeredEvents.find((e) => e.id === event.id)
                        ? "bg-green-700 cursor-not-allowed"
                        : "bg-blue-600 hover:bg-blue-700 text-white"
                        }`}
                      disabled={registeredEvents.find((e) => e.id === event.id)}
                    >
                      {registeredEvents.find((e) => e.id === event.id) ? "‚úÖ Registered" : "Register"}
                    </button>

                    <button
                      onClick={() => setSelectedEvent(event)}
                      className="flex-1 py-2 rounded-lg bg-gray-700 hover:bg-gray-600 text-white font-semibold transition"
                    >
                      View Details
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Registered Events */}
          <section id="registered" className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-blue-400">My Registered Events</h3>
            {registeredEvents.length === 0 ? (
              <p className="text-gray-400">You haven‚Äôt registered for any events yet.</p>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                {registeredEvents.map((event) => (
                  <div
                    key={event.id}
                    className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-green-400/20 transition-all"
                  >
                    <h4 className="text-lg font-semibold text-white mb-2">{event.title}</h4>
                    <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                    <p className="text-gray-400 text-sm">Status: ‚úÖ Registered</p>
                  </div>
                ))}
              </div>
            )}
          </section>

          {/* Past Events */}
          {/* <section id="past-events" className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-blue-400">Past Events</h3>
            {eventsData.filter(event => new Date(event.date) < new Date()).length === 0 ? (
              <p className="text-gray-400">No past events yet.</p>
            ) : (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                {eventsData
                  .filter(event => new Date(event.date) < new Date())
                  .map(event => (
                    <div
                      key={event.id}
                      className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-gray-500/30 transition-all"
                    >
                      <h4 className="text-lg font-semibold text-white mb-2">{event.title}</h4>
                      <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                      <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                      <p className="text-gray-400 text-sm mb-2">üè∑Ô∏è {event.category}</p>
                      <p className="text-green-400 font-semibold">Status: ‚úÖ Completed</p>
                    </div>
                  ))}
              </div>
            )}
          </section> */}
          <section id="past-events" className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-blue-400">Past Events</h3>
            {eventsData?.map((event, id) => {
              <div
                key={event._id}
                className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-gray-500/30 transition-all"
              >
                <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.strength}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.shortDesc}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.about}</p>
                <p className="text-gray-400 text-sm mb-1">üìç {event.learning}</p>

              </div>

            })}
          </section>
        </div>

        {/* Sidebar */}
        <div className="w-full md:w-1/3 space-y-8">
          {/* Search & Filter */}
          <div className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Search & Filter</h3>
            <input
              type="text"
              placeholder="Search by keyword..."
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <select
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            >
              <option value="">All Categories</option>
              <option value="Technology">Technology</option>
              <option value="Culture">Culture</option>
              <option value="Entertainment">Entertainment</option>
            </select>
            <input
              type="date"
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <button
              onClick={() => { setSearch(""); setCategory(""); setDate(""); }}
              className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition"
            >
              Clear Filters
            </button>
          </div>

          {/* Profile Section */}
          <div id="profile" className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Profile</h3>
            <div className="flex items-center space-x-4 mb-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center font-bold text-lg">U</div>
              <div>
                <p className="font-semibold text-white">User Name</p>
                <p className="text-gray-400 text-sm">user@email.com</p>
              </div>
            </div>
            <button className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">Edit Profile</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default UserDashboard;
