import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import EventCard from "../components/EventCard";

const events = [
  { id: 1, title: "Hackathon 2025, AI Workshop, National Seminar on Data Science.", date: "20–21 Oct 2025, 9:00 AM", image: "/hack.jpg",venue:"Computer Science Lab, Block A",description:"A 24-hour coding challenge where students collaborate to build innovative solutions for real-world problems."},
  {
  id: 2,
  title: "Cybersecurity Workshop 2025, Blockchain Seminar, Cloud Computing Bootcamp",
  date: "5–6 Nov 2025, 10:00 AM",
  image: "/h.jpg",
  venue: "Information Technology Lab, Block B",
  description: "An interactive workshop covering the latest trends in cybersecurity, blockchain technology, and cloud computing, with hands-on sessions for participants."
},
  {
  id: 3,
  title: "Pre-Placement Talk 2025 – Career Guidance & Industry Insights",
  date: "15 Nov 2025, 11:00 AM",
  image: "/pre.jpg",
  venue: "Auditorium, Main Block",
  description: "An informative session where industry experts provide insights into career opportunities, placement strategies, and tips to excel in interviews and group discussions."
}
,
  {
  id: 4,
  title: "Ideathon Competitions 2025 – Innovative Ideas Challenge",
  date: "25–26 Nov 2025, 10:00 AM",
  image: "/hacky.jpg",
  venue: "Innovation Lab, Block C",
  description: "A 2-day competition encouraging students to brainstorm, develop, and pitch creative solutions to real-world problems, fostering innovation and teamwork."
}
];

function Home() {
  return (
    <div>
      <Navbar />
      <Hero />
      <section className="px-8 py-12">
        <h2 className="text-2xl font-bold mb-6">Promoted events</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
          {events.map(event => (
            <EventCard key={event.id} {...event} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default Home;
