import { useRef } from "react";
import { useNavigate } from "react-router-dom";

function Navbar() {
  const searchRef = useRef(null);
  const navigate = useNavigate();

  const handleBrowseClick = (e) => {
    e.preventDefault();
    if (searchRef.current) {
      searchRef.current.focus();
      searchRef.current.scrollIntoView({ behavior: "smooth", block: "center" });
    }
  };

  // Redirect Create Event → Login
  const handleCreateClick = (e) => {
    e.preventDefault();
    navigate("/login");
  };

  // Redirect Login/Register → Login
  const handleLoginClick = (e) => {
    e.preventDefault();
    navigate("/login");
  };

  return (
    <nav className="flex items-center justify-between px-8 py-4 bg-white shadow">
      <div className="text-2xl font-bold text-red-500">GVP eventz</div>

      {/* Search Bar */}
      <input
        type="text"
        ref={searchRef}
        placeholder="Search events..."
        className="border rounded-full px-4 py-2 w-1/3"
      />

      {/* Navbar Links */}
      <div className="flex items-center gap-6">
        <a href="#" onClick={handleBrowseClick}>
          Browse Events
        </a>
        <a href="#" onClick={handleCreateClick}>
          Create Event
        </a>
        <a href="#" onClick={handleLoginClick}>
          Log in / Register
        </a>
        <div className="w-10 h-10 bg-gray-300 rounded-full"></div>
      </div>
    </nav>
  );
}

export default Navbar;
