import { useState } from "react";

function Navbar({ onSearchSubmit, onBrowseClick, onRedirectToLogin }) {
  const [query, setQuery] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (query.trim()) {
      onSearchSubmit(query); // send search query to Home
    }
  };

  return (
    <nav className="flex items-center justify-between px-8 py-4 bg-white shadow">
      <div className="text-2xl font-bold text-red-500">GVP eventz</div>

      {/* Search Bar */}
      <form onSubmit={handleSubmit} className="w-1/3">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search events..."
          className="border rounded-full px-4 py-2 w-full"
        />
      </form>

      {/* Navbar Links */}
      <div className="flex items-center gap-6">
        <a href="#" onClick={onBrowseClick}>
          Browse Events
        </a>
        <a href="#" onClick={onRedirectToLogin}>
          Create Event
        </a>
        <a href="#" onClick={onRedirectToLogin}>
          Log in / Register
        </a>
        <div className="w-10 h-10 bg-gray-300 rounded-full"></div>
      </div>
    </nav>
  );
}

export default Navbar;
