// src/pages/OrganizerDashboard.js
import React, { useState } from "react";
import EventCard from "../components/EventCard";
import PastEventCard from "../components/PastEventCard";

function OrganizerDashboard() {
  const initialEvents = [
    { id: 1, name: "React Workshop", shortDesc: "Learn React basics", date: "2025-09-10", category: "Technology", venue: "Hall A", strength: 10, about: "Deep dive into React basics and hooks.", image: "/assets/react.jpg" },
    { id: 2, name: "AI Seminar", shortDesc: "Intro to AI concepts", date: "2025-09-15", category: "Technology", venue: "Hall B", strength: 5, about: "Learn AI concepts and ML applications.", image: "/assets/ai.jpg" }
  ];

  const pastEventsData = [
    { id: 3, name: "Python Bootcamp", date: "2025-08-12", image: "/assets/python.jpg", category: "Technology", venue: "Hall D" },
    { id: 4, name: "Web Dev Meetup", date: "2025-07-20", image: "/assets/web.jpg", category: "Technology", venue: "Hall E" }
  ];

  const [events, setEvents] = useState(initialEvents);
  const [pastEvents] = useState(pastEventsData);
  const [editingEvent, setEditingEvent] = useState(null);
  const [newEvent, setNewEvent] = useState({ name: "", shortDesc: "", date: "", category: "", venue: "", strength: 0, about: "", image: "" });
  const [showCreateForm, setShowCreateForm] = useState(false);

  const handleDelete = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEvents(events.filter(ev => ev.id !== id));
    }
  };

  const handleEdit = (event) => setEditingEvent(event);

  const handleSaveEdit = () => {
    setEvents(events.map(ev => ev.id === editingEvent.id ? editingEvent : ev));
    setEditingEvent(null);
  };

  const handleCreateEvent = () => {
    const newId = events.length + pastEvents.length + 1;
    setEvents([...events, { ...newEvent, id: newId }]);
    setNewEvent({ name: "", shortDesc: "", date: "", category: "", venue: "", strength: 0, about: "", image: "" });
    setShowCreateForm(false);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Welcome, Organizer!</h1>

      {/* Create New Event */}
      <div className="mb-8">
        <button
          onClick={() => setShowCreateForm(!showCreateForm)}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700 transition"
        >
          {showCreateForm ? "Cancel" : "Create New Event"}
        </button>

        {showCreateForm && (
          <div className="mt-4 bg-white p-6 rounded-xl shadow-lg">
            <h2 className="text-xl font-bold mb-4">New Event Details</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <input type="text" placeholder="Name" value={newEvent.name} onChange={e => setNewEvent({ ...newEvent, name: e.target.value })} className="p-2 border rounded w-full"/>
              <input type="date" value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} className="p-2 border rounded w-full"/>
              <input type="text" placeholder="Category" value={newEvent.category} onChange={e => setNewEvent({ ...newEvent, category: e.target.value })} className="p-2 border rounded w-full"/>
              <input type="text" placeholder="Venue" value={newEvent.venue} onChange={e => setNewEvent({ ...newEvent, venue: e.target.value })} className="p-2 border rounded w-full"/>
              <input type="number" placeholder="Seats" value={newEvent.strength} onChange={e => setNewEvent({ ...newEvent, strength: Number(e.target.value) })} className="p-2 border rounded w-full"/>
              <input type="text" placeholder="Image URL" value={newEvent.image} onChange={e => setNewEvent({ ...newEvent, image: e.target.value })} className="p-2 border rounded w-full"/>
              <textarea placeholder="Short Description" value={newEvent.shortDesc} onChange={e => setNewEvent({ ...newEvent, shortDesc: e.target.value })} className="p-2 border rounded w-full col-span-2"/>
              <textarea placeholder="About" value={newEvent.about} onChange={e => setNewEvent({ ...newEvent, about: e.target.value })} className="p-2 border rounded w-full col-span-2"/>
            </div>
            <button onClick={handleCreateEvent} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded transition">Create Event</button>
          </div>
        )}
      </div>

      {/* Upcoming Events */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Upcoming Events</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {events.map(event => (
            editingEvent?.id === event.id ? (
              <div key={event.id} className="bg-white p-4 rounded-xl shadow-lg">
                <input type="text" value={editingEvent.name} onChange={e => setEditingEvent({ ...editingEvent, name: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <input type="date" value={editingEvent.date} onChange={e => setEditingEvent({ ...editingEvent, date: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <input type="text" value={editingEvent.category} onChange={e => setEditingEvent({ ...editingEvent, category: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <input type="text" value={editingEvent.venue} onChange={e => setEditingEvent({ ...editingEvent, venue: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <input type="number" value={editingEvent.strength} onChange={e => setEditingEvent({ ...editingEvent, strength: Number(e.target.value) })} className="p-2 border rounded w-full mb-2"/>
                <textarea value={editingEvent.shortDesc} onChange={e => setEditingEvent({ ...editingEvent, shortDesc: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <textarea value={editingEvent.about} onChange={e => setEditingEvent({ ...editingEvent, about: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <input type="text" value={editingEvent.image} onChange={e => setEditingEvent({ ...editingEvent, image: e.target.value })} className="p-2 border rounded w-full mb-2"/>
                <div className="flex gap-2">
                  <button onClick={handleSaveEdit} className="bg-blue-600 hover:bg-blue-700 px-3 py-1 rounded text-white">Save</button>
                  <button onClick={() => setEditingEvent(null)} className="bg-gray-500 hover:bg-gray-600 px-3 py-1 rounded text-white">Cancel</button>
                </div>
              </div>
            ) : (
              <EventCard key={event.id} event={event} isOrganizer
                onEdit={() => handleEdit(event)}
                onDelete={() => handleDelete(event.id)}
              />
            )
          ))}
        </div>
      </section>

      {/* Past Events */}
      <section>
        <h2 className="text-2xl font-semibold mb-4 text-gray-700">Past Events</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {pastEvents.map(event => (
            <PastEventCard key={event.id} event={event} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default OrganizerDashboard;
