import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

function OrganizerDashboard() {

  const [eventsData, setEventsData] = useState([]);
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [pastEvents, setPastEvents] = useState([]);
  const [search, setSearch] = useState("");
  const [category, setCategory] = useState("");
  const [date, setDate] = useState("");
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [recentlyRegistered, setRecentlyRegistered] = useState(null);
  const [viewStates, setViewStates] = useState({}); // For toggling view details

  useEffect(() => {
    getAllEvents();
  }, []);

  const getAllEvents = () => {
    axios
      .get(base_url + "events")
      .then((res) => {
        const allEvents = res.data.events;
        setEventsData(allEvents);

        const today = new Date();
        const upcoming = allEvents.filter((event) => new Date(event.date) >= today);
        const past = allEvents.filter((event) => new Date(event.date) < today);

        upcoming.sort((a, b) => new Date(a.date) - new Date(b.date));
        past.sort((a, b) => new Date(b.date) - new Date(a.date));

        setUpcomingEvents(upcoming);
        setPastEvents(past);
      })
      .catch((err) => {
        console.log(err.message);
      });
  };
  const [selectedEvent, setSelectedEvent] = useState(null);


  const handleRegister = (event) => {
    if (!registeredEvents.find((e) => e.id === event.id)) {
      setRegisteredEvents([...registeredEvents, event]);
      setRecentlyRegistered(event);
    } else {
      setRecentlyRegistered({ ...event, already: true });
    }
  };

  const handleLogout = () => {
    window.location.href = "/home";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-8 py-4 flex justify-between items-center shadow-lg border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">Event Coordination Engine</h1>
        <div className="flex space-x-8 text-lg">
          <a href="/home" className="hover:text-blue-300 transition">Home</a>
          <a href="#browse" className="hover:text-blue-300 transition">Browse Events</a>
          <a href="#registered" className="hover:text-blue-300 transition">My Events</a>
          <a href="#profile" className="hover:text-blue-300 transition">Profile</a>
          <button onClick={handleLogout} className="hover:text-blue-400 transition">Logout</button>
        </div>
      </nav>

      {/* Selected Event Details (Top) */}
      {selectedEvent && (
        <div className="bg-gray-800/90 border border-gray-700 p-6 rounded-2xl shadow-lg mx-10 my-4">
          <h3 className="text-2xl font-bold mb-4 text-blue-400">Event Details</h3>
          <p className="text-white font-semibold mb-1">Title: {selectedEvent.title}</p>
          <p className="text-gray-400 mb-1">Date: {selectedEvent.date}</p>
          <p className="text-gray-400 mb-1">Venue: {selectedEvent.venue}</p>
          <p className="text-gray-400 mb-3">Category: {selectedEvent.category}</p>
          <button
            onClick={() => setSelectedEvent(null)}
            className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded transition"
          >
            Close Details
          </button>
        </div>
      )}

      {/* Registration Notification (Top) */}
      {recentlyRegistered && (
        <div className={`bg-gray-800/90 border ${recentlyRegistered.already ? "border-yellow-500" : "border-green-500"} p-6 rounded-2xl shadow-lg mx-10 my-4`}>
          <p className={`text-lg font-semibold mb-2 ${recentlyRegistered.already ? "text-yellow-400" : "text-green-400"}`}>
            {recentlyRegistered.already
              ? `‚ö†Ô∏è You already registered for ${recentlyRegistered.title}`
              : `‚úÖ Successfully registered for ${recentlyRegistered.title}`}
          </p>
          <button
            onClick={() => setRecentlyRegistered(null)}
            className="bg-red-600 hover:bg-red-700 text-white py-1 px-3 rounded transition"
          >
            Close
          </button>
        </div>
      )}

      {/* Hero Section */}
      <section className="text-center py-12">
        <h2 className="text-4xl font-bold text-blue-400 tracking-wide">
          Welcome, <span className="text-white">Organizer</span>
        </h2>
        <p className="text-gray-400 mt-3 text-lg">
          Manage and create events seamlessly
        </p>
        <button
          onClick={() =>
            document.getElementById("browse").scrollIntoView({ behavior: "smooth" })
          }
          className="mt-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl font-semibold shadow-lg transition"
        >
          Browse Events
        </button>
      </section>

      <div className="flex flex-col md:flex-row px-10 gap-10 pb-12">
        {/* Main Content */}
        <div className="flex-1">

          <div className="flex flex-col gap-10">
            {/* Upcoming Events */}
            <section id="browse" className="mb-12">
              <h3 className="text-2xl font-bold mb-6 text-blue-400">Upcoming Events</h3>
              {upcomingEvents.length === 0 ? (
                <p className="text-gray-400">No upcoming events right now.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {upcomingEvents.map((event) => (
                    <div key={event._id} className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-400/20 transition-all">
                      <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                      <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                      <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                      <p className="text-gray-400 text-sm mb-1">üìù {event.shortDesc}</p>
                      <div className="flex gap-2 mt-3">
                        <button onClick={() => handleEditClick(event)} className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded transition">Edit</button>
                        <button onClick={() => handleDeleteEvent(event._id)} className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded transition">Delete</button>
                        <button onClick={() => setViewStates((prev) => ({ ...prev, [event._id]: !prev[event._id] }))} className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded transition">
                          {viewStates[event._id] ? "Hide Details" : "View"}
                        </button>
                      </div>
                      {viewStates[event._id] && (
                        <div className="mt-4 bg-gray-700/50 p-3 rounded-lg text-gray-300 text-sm space-y-2">
                          <p><strong>About Event:</strong> {event.about}</p>
                          <p><strong>Learning Outcomes:</strong> {event.learning}</p>
                          <p><strong>Full Description:</strong> {event.shortDesc}</p>
                          <p><strong>Strength:</strong> {event.strength}</p>
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </section>

            {/* Past Events */}
            <section id="past-events" className="mb-12">
              <h3 className="text-2xl font-bold mb-6 text-blue-400">Past Events</h3>
              {pastEvents.length === 0 ? (
                <p className="text-gray-400">No past events yet.</p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {pastEvents.map((event) => (
                    <div key={event._id} className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-gray-500/30 transition-all">
                      <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                      <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                      <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                      <p className="text-gray-400 text-sm mb-1">üë• Strength: {event.strength}</p>
                      <p className="text-gray-400 text-sm mb-1">üìù {event.shortDesc}</p>
                    </div>
                  ))}
                </div>
              )}
            </section>
          </div>
        </div>

        {/* Sidebar */}
        <div className="w-full md:w-1/3 space-y-8">
          {/* Search & Filter */}
          <div className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Search & Filter</h3>
            <input
              type="text"
              placeholder="Search by keyword..."
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <input
              type="date"
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <button
              onClick={() => { setSearch(""); setCategory(""); setDate(""); }}
              className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition"
            >
              Clear Filters
            </button>
          </div>

          {/* Profile Section */}
          <div id="profile" className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Profile</h3>
            <div className="flex items-center space-x-4 mb-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center font-bold text-lg">O</div>
              <div>
                <p className="font-semibold text-white">Organizer Name</p>
                <p className="text-gray-400 text-sm">organizer@email.com</p>
              </div>
            </div>
            <button className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">Edit Profile</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default OrganizerDashboard;
