import { useState } from "react";

const sampleEvents = [
  {
    id: 1,
    title: "Tech Innovators Summit",
    date: "2025-10-25",
    venue: "Main Auditorium",
    description:
      "Explore the future of technology with industry leaders and innovators.",
  },
  {
    id: 2,
    title: "Cultural Fest 2025",
    date: "2025-11-05",
    venue: "Open Grounds",
    description:
      "An annual celebration of art, music, and dance with performances from across the state.",
  },
  {
    id: 3,
    title: "Startup Pitch Arena",
    date: "2025-12-10",
    venue: "Innovation Hall",
    description:
      "Entrepreneurs pitch their startups to investors and industry experts.",
  },
];

function UserDashboard() {
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [registeredEvents, setRegisteredEvents] = useState([]);

  const handleRegister = (id) => {
    if (!registeredEvents.includes(id)) {
      setRegisteredEvents([...registeredEvents, id]);
      alert("‚úÖ You have successfully registered for this event!");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white p-8">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-400">
        üéüÔ∏è Upcoming Events
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {sampleEvents.map((event) => (
          <div
            key={event.id}
            className={`rounded-2xl shadow-lg p-6 transform transition duration-300 hover:-translate-y-2 hover:shadow-2xl ${
              selectedEvent?.id === event.id
                ? "bg-gray-800 border border-blue-500"
                : "bg-gray-850 border border-gray-700"
            }`}
          >
            <h2 className="text-xl font-semibold text-blue-300 mb-2">
              {event.title}
            </h2>
            <p className="text-sm text-gray-400 mb-1">üìÖ {event.date}</p>
            <p className="text-sm text-gray-400 mb-3">üìç {event.venue}</p>

            {selectedEvent?.id === event.id ? (
              <>
                <p className="text-gray-300 mb-4">{event.description}</p>
                <button
                  onClick={() => handleRegister(event.id)}
                  disabled={registeredEvents.includes(event.id)}
                  className={`px-4 py-2 rounded-lg transition duration-300 ${
                    registeredEvents.includes(event.id)
                      ? "bg-green-700 cursor-not-allowed"
                      : "bg-blue-600 hover:bg-blue-700"
                  }`}
                >
                  {registeredEvents.includes(event.id)
                    ? "‚úÖ Registered"
                    : "Register Now"}
                </button>
              </>
            ) : (
              <button
                onClick={() => setSelectedEvent(event)}
                className="bg-transparent border border-blue-500 text-blue-400 px-4 py-2 rounded-lg hover:bg-blue-600 hover:text-white transition duration-300"
              >
                View Details
              </button>
            )}
          </div>
        ))}
      </div>

      {/* Inline Event Details Panel */}
      {selectedEvent && (
        <div className="mt-10 bg-gray-800 p-6 rounded-2xl shadow-xl border border-blue-600">
          <h2 className="text-2xl font-semibold text-blue-400 mb-2">
            {selectedEvent.title}
          </h2>
          <p className="text-gray-400 mb-1">üìÖ {selectedEvent.date}</p>
          <p className="text-gray-400 mb-3">üìç {selectedEvent.venue}</p>
          <p className="text-gray-300">{selectedEvent.description}</p>

          <div className="mt-5">
            <button
              onClick={() => handleRegister(selectedEvent.id)}
              disabled={registeredEvents.includes(selectedEvent.id)}
              className={`px-5 py-2 rounded-lg transition duration-300 ${
                registeredEvents.includes(selectedEvent.id)
                  ? "bg-green-700 cursor-not-allowed"
                  : "bg-blue-600 hover:bg-blue-700"
              }`}
            >
              {registeredEvents.includes(selectedEvent.id)
                ? "‚úÖ You Are Registered"
                : "Register for Event"}
            </button>

            <button
              onClick={() => setSelectedEvent(null)}
              className="ml-4 px-5 py-2 rounded-lg border border-gray-500 text-gray-300 hover:bg-gray-700 transition duration-300"
            >
              Back to Events
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
