import React, { useState } from "react";

const initialEvents = [
  {
    id: 1,
    title: "Hackathon 2025 – AI & Innovation",
    date: "20–21 Oct 2025, 9:00 AM",
    venue: "Computer Science Lab, Block A",
    seats: 30,
    description:
      "A 24-hour coding marathon to create AI-powered solutions for real-world challenges. Teams collaborate and innovate using advanced technologies like Python, ML, and APIs.",
  },
  {
    id: 2,
    title: "Cybersecurity & Blockchain Workshop",
    date: "5–6 Nov 2025, 10:00 AM",
    venue: "IT Lab, Block B",
    seats: 25,
    description:
      "Hands-on learning in cybersecurity and blockchain. Build secure systems, explore digital ledger concepts, and learn how data protection works in the real world.",
  },
  {
    id: 3,
    title: "Pre-Placement Talk 2025",
    date: "15 Nov 2025, 11:00 AM",
    venue: "Auditorium, Main Block",
    seats: 40,
    description:
      "A seminar with industry experts offering guidance on placement preparation, resume building, and technical interviews to help students crack top company selections.",
  },
  {
    id: 4,
    title: "Ideathon – Innovative Ideas Challenge",
    date: "25–26 Nov 2025, 10:00 AM",
    venue: "Innovation Lab, Block C",
    seats: 20,
    description:
      "A 2-day event for creative problem solvers. Teams brainstorm, design, and pitch unique solutions to real-world problems using innovation and technology.",
  },
];

function UserDashboard() {
  const [events, setEvents] = useState(initialEvents);
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedEvent, setSelectedEvent] = useState(null);

  const handleRegister = (id) => {
    setEvents((prev) =>
      prev.map((event) =>
        event.id === id && event.seats > 0
          ? { ...event, seats: event.seats - 1 }
          : event
      )
    );
    const eventToAdd = events.find((event) => event.id === id);
    if (eventToAdd && !registeredEvents.find((e) => e.id === id)) {
      setRegisteredEvents([...registeredEvents, eventToAdd]);
    }
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
    setSelectedEvent(null); // reset view if searching
  };

  const filteredEvents = events.filter((event) =>
    event.title.toLowerCase().includes(searchTerm)
  );

  const handleView = (event) => {
    setSelectedEvent(event);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-950 via-sky-900 to-indigo-800 p-8 text-white">
      {/* Header */}
      <header className="flex justify-between items-center mb-8 backdrop-blur-md bg-white/10 shadow-lg rounded-2xl px-6 py-4 border border-white/20">
        <h1 className="text-3xl font-bold text-white">User Dashboard</h1>
        <input
          type="text"
          placeholder="Search events..."
          className="border border-white/30 bg-white/20 rounded-full px-4 py-2 w-1/3 focus:outline-none focus:ring focus:ring-blue-300 text-white placeholder-gray-200"
          value={searchTerm}
          onChange={handleSearch}
        />
      </header>

      {/* Selected Event Details */}
      {selectedEvent && (
        <div className="backdrop-blur-md bg-white/15 border border-white/20 rounded-2xl p-6 mb-10 shadow-xl transition-all duration-300">
          <h2 className="text-2xl font-bold text-blue-200 mb-2">
            {selectedEvent.title}
          </h2>
          <p className="text-gray-200 font-medium">{selectedEvent.date}</p>
          <p className="text-gray-300 text-sm mb-3">{selectedEvent.venue}</p>
          <p className="text-gray-100 text-base leading-relaxed mb-4">
            {selectedEvent.description}
          </p>
          <p className="text-sm text-blue-100 font-semibold mb-3">
            Seats left: {selectedEvent.seats}
          </p>
          <div className="flex gap-4">
            <button
              className={`px-6 py-2 rounded-full transition font-semibold ${
                selectedEvent.seats > 0
                  ? "bg-green-500 hover:bg-green-600"
                  : "bg-gray-500 cursor-not-allowed"
              }`}
              onClick={() => handleRegister(selectedEvent.id)}
              disabled={selectedEvent.seats === 0}
            >
              Register
            </button>
            <button
              className="bg-red-500 hover:bg-red-600 px-6 py-2 rounded-full font-semibold"
              onClick={() => setSelectedEvent(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Events Section */}
      <section>
        <h2 className="text-2xl font-semibold mb-4 text-white">
          Available Events
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredEvents.map((event) => (
            <div
              key={event.id}
              className="backdrop-blur-md bg-white/15 border border-white/20 rounded-2xl shadow-lg hover:shadow-2xl transition-transform transform hover:-translate-y-1 p-6"
            >
              <h3 className="text-xl font-bold text-blue-200 mb-2">
                {event.title}
              </h3>
              <p className="text-gray-300">{event.date}</p>
              <p className="text-gray-400 text-sm mb-4">{event.venue}</p>
              <p className="text-gray-200 text-sm mb-4 line-clamp-3">
                {event.description}
              </p>
              <p className="text-sm text-blue-100 font-semibold mb-3">
                Seats left: {event.seats}
              </p>
              <div className="flex justify-between">
                <button
                  className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded-full text-white font-semibold transition"
                  onClick={() => handleView(event)}
                >
                  View
                </button>
                <button
                  className={`px-4 py-2 rounded-full transition font-semibold ${
                    event.seats > 0
                      ? "bg-green-500 hover:bg-green-600"
                      : "bg-gray-500 cursor-not-allowed"
                  } text-white`}
                  onClick={() => handleRegister(event.id)}
                  disabled={event.seats === 0}
                >
                  Register
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Registered Events */}
      <section className="mt-12">
        <h2 className="text-2xl font-semibold mb-4 text-white">
          Registered Events
        </h2>
        {registeredEvents.length === 0 ? (
          <p className="text-gray-300">You haven’t registered for any events yet.</p>
        ) : (
          <ul className="space-y-3">
            {registeredEvents.map((event) => (
              <li
                key={event.id}
                className="p-4 backdrop-blur-md bg-white/15 border border-white/20 rounded-lg shadow-sm text-gray-100"
              >
                <span className="font-medium text-blue-200">{event.title}</span>{" "}
                — <span className="text-gray-300">{event.date}</span>
              </li>
            ))}
          </ul>
        )}
      </section>
    </div>
  );
}

export default UserDashboard;
