import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

// Compact Event Calendar Component
function CompactEventCalendar({ events = [] }) {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(null);
  const [showEventDetails, setShowEventDetails] = useState(false);

  const eventDates = events.map((event) => {
    const dateOnly = event.date.split("T")[0];
    return dateOnly;
  });

  const getEventsForDate = (dateStr) => {
    return events.filter((event) => {
      const eventDateOnly = event.date.split("T")[0];
      return eventDateOnly === dateStr;
    });
  };

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const startingDayOfWeek = new Date(year, month, 1).getDay();

  const monthNames = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];
  const dayNames = ["S", "M", "T", "W", "T", "F", "S"];

  const previousMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
    setSelectedDate(null);
    setShowEventDetails(false);
  };

  const nextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
    setSelectedDate(null);
    setShowEventDetails(false);
  };

  const handleDateClick = (day) => {
    const y = currentDate.getFullYear();
    const m = String(currentDate.getMonth() + 1).padStart(2, "0");
    const d = String(day).padStart(2, "0");
    const dateStr = `${y}-${m}-${d}`;

    setSelectedDate(dateStr);
    setShowEventDetails(true);
  };

  const calendarDays = [];

  for (let i = 0; i < startingDayOfWeek; i++) {
    calendarDays.push(
      <div key={`empty-${i}`} className="aspect-square" />
    );
  }

  for (let day = 1; day <= daysInMonth; day++) {
    const monthStr = String(month + 1).padStart(2, "0");
    const dayStr = String(day).padStart(2, "0");
    const dateStr = `${year}-${monthStr}-${dayStr}`;

    const hasEvent = eventDates.includes(dateStr);

    const today = new Date();
    const todayStr = `${today.getFullYear()}-${String(
      today.getMonth() + 1
    ).padStart(2, "0")}-${String(today.getDate()).padStart(2, "0")}`;
    const isToday = dateStr === todayStr;

    const isSelected = selectedDate === dateStr;

    calendarDays.push(
      <button
        key={day}
        onClick={() => handleDateClick(day)}
        className={`aspect-square p-1 rounded text-xs font-medium transition-all relative flex items-center justify-center
          ${isToday ? "ring-1 ring-blue-400" : ""}
          ${isSelected ? "bg-blue-600 text-white" : ""}
          ${
            hasEvent && !isSelected
              ? "bg-green-500 text-white font-bold"
              : ""
          }
          ${
            !hasEvent && !isSelected
              ? "text-gray-400 hover:bg-gray-700/30"
              : ""
          }`}
      >
        {day}
      </button>
    );
  }

  const selectedDayEvents = selectedDate
    ? getEventsForDate(selectedDate)
    : [];

  return (
    <div className="bg-gray-800/90 border border-gray-700 rounded-xl shadow-lg p-3 w-64">
      <div className="flex items-center justify-between mb-3">
        <button
          onClick={previousMonth}
          className="p-1 hover:bg-gray-700/50 rounded transition"
        >
          <svg
            className="w-4 h-4 text-gray-400"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <polyline points="15 18 9 12 15 6" strokeWidth="2" />
          </svg>
        </button>

        <h4 className="text-sm font-semibold text-white">
          {monthNames[month]} {year}
        </h4>

        <button
          onClick={nextMonth}
          className="p-1 hover:bg-gray-700/50 rounded transition"
        >
          <svg
            className="w-4 h-4 text-gray-400"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <polyline points="9 18 15 12 9 6" strokeWidth="2" />
          </svg>
        </button>
      </div>

      <div className="grid grid-cols-7 gap-1 mb-1">
        {dayNames.map((day) => (
          <div
            key={day}
            className="text-center text-xs font-semibold text-gray-500"
          >
            {day}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-1 mb-3">{calendarDays}</div>

      <div className="flex items-center justify-center gap-3 pt-2 border-t border-gray-700/50 text-xs">
        <div className="flex items-center gap-1">
          <div className="w-3 h-3 rounded bg-green-500" />
          <span className="text-gray-400">Event</span>
        </div>
        <div className="flex items-center gap-1">
          <div className="w-3 h-3 rounded ring-1 ring-blue-400" />
          <span className="text-gray-400">Today</span>
        </div>
      </div>

      {showEventDetails && selectedDate && (
        <div className="mt-3 pt-3 border-t border-gray-700/50">
          <div className="flex justify-between items-center mb-2">
            <h5 className="text-xs font-semibold text-gray-300">
              {new Date(
                selectedDate + "T00:00:00"
              ).toLocaleDateString("en-US", {
                month: "short",
                day: "numeric",
              })}
            </h5>
            <button
              onClick={() => setShowEventDetails(false)}
              className="text-gray-500 hover:text-gray-300"
            >
              <svg
                className="w-3 h-3"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
          </div>

          {selectedDayEvents.length === 0 ? (
            <p className="text-xs text-gray-500 italic">
              No events
            </p>
          ) : (
            <div className="space-y-1 max-h-32 overflow-y-auto">
              {selectedDayEvents.map((event) => (
                <div
                  key={event._id || event.id}
                  className="bg-gray-700/40 p-2 rounded text-xs"
                >
                  <p className="text-white font-medium truncate">
                    {event.name}
                  </p>
                  <p className="text-gray-400 truncate">
                    {event.venue}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

function UserDashboard() {
  const [eventsData, setEventsData] = useState([]);
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [pastEvents, setPastEvents] = useState([]);

  const [date, setDate] = useState("");

  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [attendedEvents, setAttendedEvents] = useState([]);

  const [recentlyRegistered, setRecentlyRegistered] = useState(null);
  const [viewStates, setViewStates] = useState({});

  const [showEventForm, setShowEventForm] = useState(false);

  const [feedbackInputs, setFeedbackInputs] = useState({});
  const [feedbackList, setFeedbackList] = useState({});
  const [loadingFeedback, setLoadingFeedback] = useState({});

  // Tabs and filters
  const [activeTab, setActiveTab] = useState("all"); // "all" | "registered" | "attended"
  const [filters, setFilters] = useState({
    date: "",
    name: "",
    venue: "",
  });

  // Confirmation modal
  const [confirmEvent, setConfirmEvent] = useState(null);
  const [showConfirm, setShowConfirm] = useState(false);

  // Event form states
  const [editEvent, setEditEvent] = useState(null);
  const [name, setName] = useState("");
  const [eventDate, setEventDate] = useState("");
  const [venue, setVenue] = useState("");
  const [strength, setStrength] = useState("");
  const [shortDesc, setShortDesc] = useState("");
  const [about, setAbout] = useState("");
  const [learning, setLearning] = useState("");

  // Venues for select
  const [venues, setVenues] = useState([]);
  const [loadingVenues, setLoadingVenues] = useState(false);
  const [useCustomVenue, setUseCustomVenue] = useState(false);

  const [notificationsData, setNotificationsData] = useState([]);
  const [loadingNotifications, setLoadingNotifications] =
    useState(false);
  const [notificationsError, setNotificationsError] = useState("");

  const handleNotifications = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login again");
      return;
    }
    setLoadingNotifications(true);
    axios
      .get(base_url + "notifications", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) =>
        setNotificationsData(res.data.notifications || [])
      )
      .catch((err) => {
        console.error(err);
        alert(
          err.response?.data?.message ||
            "Failed to load notifications"
        );
      })
      .finally(() => setLoadingNotifications(false));
  };

  const handleMarkOneRead = (id) => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login again");
      return;
    }
    axios
      .put(
        base_url + `notifications/${id}/read`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then(() => {
        setNotificationsData((prev) =>
          prev.map((n) =>
            n._id === id ? { ...n, isRead: true } : n
          )
        );
      })
      .catch((err) => {
        console.error(err);
        alert(
          err.response?.data?.message ||
            "Failed to update notification"
        );
      });
  };

  const handleMarkAllRead = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please login again");
      return;
    }
    axios
      .put(
        base_url + "notifications/read-all",
        {},
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      )
      .then(() => {
        setNotificationsData((prev) =>
          prev.map((n) => ({ ...n, isRead: true }))
        );
      })
      .catch((err) => {
        console.error(err);
        alert(
          err.response?.data?.message ||
            "Failed to update notifications"
        );
      });
  };

  // decode userId from JWT
  const getUserIdFromToken = () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return null;
      const payload = JSON.parse(atob(token.split(".")[1]));
      return payload.userId;
    } catch (e) {
      return null;
    }
  };

  const loadVenues = () => {
    setLoadingVenues(true);
    axios
      .get(base_url + "venues")
      .then((res) => setVenues(res.data.venues || []))
      .catch((err) => {
        console.error(err);
      })
      .finally(() => setLoadingVenues(false));
  };

  useEffect(() => {
    getAllEvents();
    fetchRegisteredEventsFromBackend();
    fetchAttendedEventsFromBackend();
    loadVenues();
  }, []);

  // ====== FEEDBACK LOGIC ======

  const loadFeedbackForEvent = async (eventId) => {
    try {
      setLoadingFeedback((prev) => ({ ...prev, [eventId]: true }));

      const res = await axios.get(
        `${base_url}events/${eventId}/feedback`
      );

      setFeedbackList((prev) => ({
        ...prev,
        [eventId]: res.data.feedbacks || [],
      }));
    } catch (err) {
      console.error("Error fetching feedback", err);
    } finally {
      setLoadingFeedback((prev) => ({
        ...prev,
        [eventId]: false,
      }));
    }
  };

  const submitFeedback = async (eventId) => {
    const text = feedbackInputs[eventId]?.trim();

    if (!text) {
      alert("Please enter feedback");
      return;
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please login again");
        return;
      }

      await axios.post(
        `${base_url}events/${eventId}/feedback`,
        { feedback: text },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setFeedbackInputs((prev) => ({
        ...prev,
        [eventId]: "",
      }));
      await loadFeedbackForEvent(eventId);
    } catch (err) {
      console.error("Error submitting feedback", err);
      const msg =
        err.response?.data?.message || "Failed to submit feedback";
      alert(msg);
    }
  };

  // ====== EVENTS / REGISTRATIONS LOGIC (existing code) ======
  const getAllEvents = () => {
    axios
      .get(base_url + "events")
      .then((res) => {
        const allEvents = res.data.events || [];
        setEventsData(allEvents);

        const today = new Date();
        const upcoming = allEvents.filter(
          (event) => new Date(event.date) >= today
        );
        const past = allEvents.filter(
          (event) => new Date(event.date) < today
        );

        upcoming.sort(
          (a, b) => new Date(a.date) - new Date(b.date)
        );
        past.sort((a, b) => new Date(b.date) - new Date(a.date));

        setUpcomingEvents(upcoming);
        setPastEvents(past);
      })
      .catch((err) => console.log(err.message));
  };

  const fetchRegisteredEventsFromBackend = async () => {
    try {
      const userId = getUserIdFromToken();
      if (!userId) return;

      const token = localStorage.getItem("token");
      const res = await axios.get(
        `${base_url}users/${userId}/registrations`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRegisteredEvents(res.data.events || []);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchAttendedEventsFromBackend = async () => {
    try {
      const userId = getUserIdFromToken();
      if (!userId) return;

      const token = localStorage.getItem("token");
      const res = await axios.get(
        `${base_url}users/${userId}/attended`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAttendedEvents(res.data.events || []);
    } catch (err) {
      console.error(err);
    }
  };

  const isEventFilled = (event) => {
    const current = event.currentRegistrations || 0;
    const capacity = event.strength || 0;
    return current >= capacity;
  };

  const handleRegisterClick = (event) => {
    setConfirmEvent(event);
    setShowConfirm(true);
  };

  const handleConfirmRegister = async () => {
    if (!confirmEvent) return;
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please login again");
        return;
      }
      await axios.post(
        `${base_url}events/${confirmEvent._id}/register`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRecentlyRegistered(confirmEvent._id);
      setShowConfirm(false);
      setConfirmEvent(null);
      fetchRegisteredEventsFromBackend();
      getAllEvents();
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message ||
          "Failed to register for event"
      );
    }
  };

  const resetEventForm = () => {
    setEditEvent(null);
    setName("");
    setEventDate("");
    setVenue("");
    setStrength("");
    setShortDesc("");
    setAbout("");
    setLearning("");
    setUseCustomVenue(false);
  };

  const handleSaveEvent = async (e) => {
    e.preventDefault();
    if (
      !name ||
      !eventDate ||
      !venue ||
      !strength ||
      !shortDesc ||
      !about ||
      !learning
    ) {
      alert("Please fill all fields");
      return;
    }

    const eventData = {
      name,
      date: eventDate,
      venue,
      strength: parseInt(strength),
      shortDesc,
      about,
      learning,
    };

    const token = localStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };

    if (editEvent) {
      axios
        .put(
          base_url + `events/${editEvent._id}`,
          eventData,
          config
        )
        .then((res) => {
          alert(
            res.data.message || "Event updated successfully!"
          );
          resetEventForm();
          setShowEventForm(false);
          getAllEvents();
        })
        .catch((err) => {
          console.log(err);
          alert(
            err.response?.data?.message ||
              "Failed to update event"
          );
        });
    } else {
      axios
        .post(base_url + "events", eventData, config)
        .then((res) => {
          alert(
            res.data.message || "Event created successfully!"
          );
          resetEventForm();
          setShowEventForm(false);
          getAllEvents();
        })
        .catch((err) => {
          console.log(err);
          alert(
            err.response?.data?.message ||
              "Failed to create event"
          );
        });
    }
  };

  const handleEditEvent = (event) => {
    setEditEvent(event);
    setName(event.name);
    setEventDate(
      typeof event.date === "string"
        ? event.date.slice(0, 10)
        : new Date(event.date).toISOString().slice(0, 10)
    );
    setVenue(event.venue);
    setStrength(event.strength);
    setShortDesc(event.shortDesc);
    setAbout(event.about);
    setLearning(event.learning);
    setShowEventForm(true);

    // If this venue is not in list, treat as custom
    if (!venues.find((v) => v.name === event.venue)) {
      setUseCustomVenue(true);
    } else {
      setUseCustomVenue(false);
    }
  };

  const handleDeleteEvent = async (eventId) => {
    if (
      !window.confirm(
        "Are you sure you want to delete this event?"
      )
    )
      return;
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please login again");
        return;
      }
      await axios.delete(base_url + `events/${eventId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Event deleted successfully");
      getAllEvents();
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message || "Failed to delete event"
      );
    }
  };

  const applyFilters = (events) => {
    return events.filter((event) => {
      const matchesDate = filters.date
        ? event.date.startsWith(filters.date)
        : true;
      const matchesName = filters.name
        ? event.name
            .toLowerCase()
            .includes(filters.name.toLowerCase())
        : true;
      const matchesVenue = filters.venue
        ? event.venue
            .toLowerCase()
            .includes(filters.venue.toLowerCase())
        : true;
      return matchesDate && matchesName && matchesVenue;
    });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  const filteredUpcomingEvents = upcomingEvents;
  const filteredPastEvents = pastEvents;

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-950 via-gray-900 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950/90 backdrop-blur-md border-b border-gray-800 sticky top-0 z-20">
        <div className="max-w-7xl mx-auto px-6 py-4 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="bg-blue-600/20 p-2 rounded-lg border border-blue-500/40">
              <svg
                className="w-7 h-7 text-blue-400"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M9.75 17L9 20l-1 1h8l-1-1-.75-3M3 13h18M5 10V6a2 2 0 012-2h10a2 2 0 012 2v4"
                />
              </svg>
            </div>
            <div>
              <h1 className="text-2xl font-bold text-blue-400">
                Student Dashboard
              </h1>
              <p className="text-xs text-gray-400">
                Explore, register, and track your event journey
              </p>
            </div>
          </div>

          <div className="flex items-center gap-4">
            <button
              onClick={handleNotifications}
              className="relative bg-gray-800 hover:bg-gray-700 text-gray-200 px-3 py-1.5 rounded-lg text-xs flex items-center gap-2 border border-gray-700"
            >
              <svg
                className="w-4 h-4 text-blue-300"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6 6 0 10-12 0v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9"
                />
              </svg>
              <span>Notifications</span>
              {notificationsData.some((n) => !n.isRead) && (
                <span className="absolute -top-1 -right-1 w-2 h-2 rounded-full bg-red-500"></span>
              )}
            </button>

            <button
              onClick={handleLogout}
              className="bg-red-600 hover:bg-red-700 text-white px-4 py-1.5 rounded-lg text-sm font-semibold"
            >
              Logout
            </button>
          </div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto px-6 py-8 grid grid-cols-1 lg:grid-cols-[260px,1fr] gap-8">
        {/* Left column (calendar + quick stats) */}
        <aside className="space-y-6">
          <CompactEventCalendar events={eventsData} />

          <div className="bg-gray-900/80 border border-gray-800 rounded-xl p-4 shadow-lg">
            <h3 className="text-sm font-semibold text-gray-200 mb-3">
              Your Activity
            </h3>
            <div className="grid grid-cols-2 gap-3 text-xs">
              <div className="bg-gray-800/80 rounded-lg p-3 border border-gray-700">
                <p className="text-gray-400 mb-1">
                  Registered Events
                </p>
                <p className="text-lg font-bold text-blue-400">
                  {registeredEvents.length}
                </p>
              </div>
              <div className="bg-gray-800/80 rounded-lg p-3 border border-gray-700">
                <p className="text-gray-400 mb-1">
                  Attended Events
                </p>
                <p className="text-lg font-bold text-green-400">
                  {attendedEvents.length}
                </p>
              </div>
            </div>
          </div>
        </aside>

        {/* Right column main content */}
        <section className="space-y-8">
          {/* Top header / tabs */}
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h2 className="text-2xl font-bold text-white">
                Events
              </h2>
              <p className="text-sm text-gray-400">
                Browse all upcoming events, manage your
                registrations, and track attendance.
              </p>
            </div>

            <div className="flex flex-wrap items-center gap-2">
              <button
                onClick={() => setShowEventForm((prev) => !prev)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm font-semibold flex items-center gap-2"
              >
                <span>Propose Event</span>
              </button>
            </div>
          </div>

          {/* Event form (for proposing event) */}
          {showEventForm && (
            <div className="bg-gray-900/80 border border-gray-800 rounded-2xl p-5 shadow-xl">
              <div className="flex items-center justify-between mb-3">
                <h3 className="text-lg font-semibold text-blue-400">
                  {editEvent
                    ? "Edit Proposed Event"
                    : "Propose New Event"}
                </h3>
                <button
                  onClick={() => {
                    setShowEventForm(false);
                    resetEventForm();
                  }}
                  className="text-gray-400 hover:text-white text-sm"
                >
                  Close
                </button>
              </div>
              <p className="text-xs text-gray-500 mb-4">
                Proposed events will be reviewed by the admin
                before publishing.
              </p>

              <form
                onSubmit={handleSaveEvent}
                className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm"
              >
                <div className="md:col-span-2">
                  <label className="block text-xs text-gray-400 mb-1">
                    Event Name
                  </label>
                  <input
                    type="text"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Enter event title"
                  />
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Date
                  </label>
                  <input
                    type="date"
                    value={eventDate}
                    onChange={(e) =>
                      setEventDate(e.target.value)
                    }
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                {/* Venue select + Others */}
                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Venue
                  </label>
                  <select
                    value={useCustomVenue ? "other" : venue}
                    onChange={(e) => {
                      if (e.target.value === "other") {
                        setUseCustomVenue(true);
                        setVenue("");
                      } else {
                        setUseCustomVenue(false);
                        setVenue(e.target.value);
                      }
                    }}
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                  >
                    <option value="">Select a venue</option>
                    {venues.map((v) => (
                      <option key={v._id} value={v.name}>
                        {v.name}
                      </option>
                    ))}
                    <option value="other">
                      Others (manual entry)
                    </option>
                  </select>
                  {useCustomVenue && (
                    <input
                      type="text"
                      placeholder="Enter venue manually"
                      value={venue}
                      onChange={(e) =>
                        setVenue(e.target.value)
                      }
                      className="mt-2 w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  )}
                  {loadingVenues && (
                    <p className="text-[10px] text-gray-500 mt-1">
                      Loading venues...
                    </p>
                  )}
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Expected Participants
                  </label>
                  <input
                    type="number"
                    value={strength}
                    onChange={(e) =>
                      setStrength(e.target.value)
                    }
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g., 100"
                  />
                </div>

                <div className="md:col-span-2">
                  <label className="block text-xs text-gray-400 mb-1">
                    Short Description
                  </label>
                  <textarea
                    value={shortDesc}
                    onChange={(e) =>
                      setShortDesc(e.target.value)
                    }
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500 h-16 resize-none"
                    placeholder="Brief summary of the event"
                  />
                </div>

                <div className="md:col-span-2">
                  <label className="block text-xs text-gray-400 mb-1">
                    About Event
                  </label>
                  <textarea
                    value={about}
                    onChange={(e) => setAbout(e.target.value)}
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500 h-20 resize-none"
                    placeholder="Detailed description, agenda, speakers, etc."
                  />
                </div>

                <div className="md:col-span-2">
                  <label className="block text-xs text-gray-400 mb-1">
                    Learning Outcomes
                  </label>
                  <textarea
                    value={learning}
                    onChange={(e) =>
                      setLearning(e.target.value)
                    }
                    className="w-full bg-gray-800 border border-gray-700 rounded-lg px-3 py-2 text-white focus:outline-none focus:ring-2 focus:ring-blue-500 h-20 resize-none"
                    placeholder="What will participants learn or gain?"
                  />
                </div>

                <div className="md:col-span-2 flex justify-end gap-3 pt-2">
                  {editEvent && (
                    <button
                      type="button"
                      onClick={resetEventForm}
                      className="px-4 py-2 rounded-lg bg-gray-700 hover:bg-gray-600 text-sm"
                    >
                      Cancel Edit
                    </button>
                  )}
                  <button
                    type="submit"
                    className="px-5 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 text-sm font-semibold"
                  >
                    {editEvent ? "Update Event" : "Submit Proposal"}
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Filters */}
          <div className="bg-gray-900/80 border border-gray-800 rounded-2xl p-4 shadow-lg">
            <div className="flex flex-wrap items-center justify-between gap-3 mb-3">
              <div className="flex gap-2 text-xs font-medium bg-gray-800/80 rounded-full p-1">
                <button
                  className={`px-3 py-1 rounded-full ${
                    activeTab === "all"
                      ? "bg-blue-600 text-white"
                      : "text-gray-300 hover:bg-gray-700"
                  }`}
                  onClick={() => setActiveTab("all")}
                >
                  All Events
                </button>
                <button
                  className={`px-3 py-1 rounded-full ${
                    activeTab === "registered"
                      ? "bg-blue-600 text-white"
                      : "text-gray-300 hover:bg-gray-700"
                  }`}
                  onClick={() => setActiveTab("registered")}
                >
                  Registered
                </button>
                <button
                  className={`px-3 py-1 rounded-full ${
                    activeTab === "attended"
                      ? "bg-blue-600 text-white"
                      : "text-gray-300 hover:bg-gray-700"
                  }`}
                  onClick={() => setActiveTab("attended")}
                >
                  Attended
                </button>
              </div>

              <div className="flex flex-wrap gap-3 items-end text-xs">
                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Filter by Date
                  </label>
                  <input
                    type="date"
                    value={filters.date}
                    onChange={(e) =>
                      setFilters((f) => ({
                        ...f,
                        date: e.target.value,
                      }))
                    }
                    className="bg-gray-800 border border-gray-600 text-white rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Search by Name
                  </label>
                  <input
                    type="text"
                    placeholder="Event name"
                    value={filters.name}
                    onChange={(e) =>
                      setFilters((f) => ({
                        ...f,
                        name: e.target.value,
                      }))
                    }
                    className="bg-gray-800 border border-gray-600 text-white rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                <div>
                  <label className="block text-xs text-gray-400 mb-1">
                    Filter by Venue
                  </label>
                  <input
                    type="text"
                    placeholder="Venue"
                    value={filters.venue}
                    onChange={(e) =>
                      setFilters((f) => ({
                        ...f,
                        venue: e.target.value,
                      }))
                    }
                    className="bg-gray-800 border border-gray-600 text-white rounded-lg px-3 py-2 text-sm focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>

                {(filters.date ||
                  filters.name ||
                  filters.venue) && (
                  <button
                    onClick={() =>
                      setFilters({
                        date: "",
                        name: "",
                        venue: "",
                      })
                    }
                    className="self-end text-xs text-red-400 underline hover:text-red-500"
                  >
                    Clear All Filters
                  </button>
                )}
              </div>
            </div>
          </div>

          {/* ===== TAB CONTENT ===== */}

          {/* All (Upcoming + Past + Feedback) */}
          {activeTab === "all" && (
            <>
              {/* Upcoming */}
              <section id="browse" className="mb-12">
                <h3 className="text-2xl font-bold mb-6 text-blue-400">
                  Upcoming Events
                </h3>

                {applyFilters(filteredUpcomingEvents).length ===
                0 ? (
                  <p className="text-gray-400">
                    No upcoming events for this selection.
                  </p>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                    {applyFilters(
                      filteredUpcomingEvents
                    ).map((event) => (
                      <div
                        key={event.id || event._id}
                        className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-400/20 transition-all"
                      >
                        <h4 className="text-lg font-semibold text-white mb-2">
                          {event.name}
                        </h4>
                        <p className="text-gray-400 text-sm mb-1">
                          {event.date}
                        </p>
                        <p className="text-gray-400 text-sm mb-1">
                          {event.venue}
                        </p>
                        <p className="text-gray-400 text-sm mb-1">
                          {event.shortDesc}
                        </p>
                        <p className="text-gray-400 text-xs mb-1">
                          {event.currentRegistrations || 0} /{" "}
                          {event.strength || 0} registered
                        </p>

                        <div className="flex gap-2 mt-3">
                          {isEventFilled(event) ? (
                            <button
                              disabled
                              className="bg-gray-600 text-gray-300 cursor-not-allowed py-2 px-4 rounded"
                            >
                              Registrations Filled
                            </button>
                          ) : (
                            <button
                              onClick={() =>
                                handleRegisterClick(event)
                              }
                              className="bg-blue-600 hover:bg-blue-700 text-white py-2 px-4 rounded transition"
                            >
                              Register
                            </button>
                          )}

                          <button
                            onClick={() =>
                              setViewStates((prev) => ({
                                ...prev,
                                [event.id || event._id]:
                                  !prev[event.id || event._id],
                              }))
                            }
                            className="bg-green-600 hover:bg-green-700 text-white py-2 px-4 rounded transition"
                          >
                            {viewStates[event.id || event._id]
                              ? "Hide Details"
                              : "View"}
                          </button>
                        </div>

                        {viewStates[event.id || event._id] && (
                          <div className="mt-3 text-sm text-gray-300 space-y-2 border-t border-gray-700 pt-3">
                            <p>
                              <span className="font-semibold">
                                About:
                              </span>{" "}
                              {event.about}
                            </p>
                            <p>
                              <span className="font-semibold">
                                Learning:
                              </span>{" "}
                              {event.learning}
                            </p>
                            <p className="text-xs text-gray-500">
                              Created by{" "}
                              {event.createdBy?.fullName ||
                                "Unknown"}
                            </p>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                )}
              </section>

              {/* Past */}
              <section id="past" className="mb-12">
                <h3 className="text-2xl font-bold mb-6 text-green-400">
                  Past Events & Feedback
                </h3>

                {applyFilters(filteredPastEvents).length === 0 ? (
                  <p className="text-gray-400">
                    No past events for this selection.
                  </p>
                ) : (
                  <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                    {applyFilters(filteredPastEvents).map(
                      (event) => (
                        <div
                          key={event.id || event._id}
                          className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-green-400/20 transition-all"
                        >
                          <h4 className="text-lg font-semibold text-white mb-2">
                            {event.name}
                          </h4>
                          <p className="text-gray-400 text-sm mb-1">
                            {event.date}
                          </p>
                          <p className="text-gray-400 text-sm mb-1">
                            {event.venue}
                          </p>
                          <p className="text-gray-400 text-sm mb-1">
                            {event.shortDesc}
                          </p>

                          <button
                            onClick={() =>
                              setViewStates((prev) => ({
                                ...prev,
                                [event.id || event._id]:
                                  !prev[event.id || event._id],
                              }))
                            }
                            className="bg-gray-700 hover:bg-gray-600 text-white py-2 px-4 rounded transition mt-3 text-sm"
                          >
                            {viewStates[event.id || event._id]
                              ? "Hide Details & Feedback"
                              : "View Details / Give Feedback"}
                          </button>

                          {viewStates[event.id || event._id] && (
                            <div className="mt-3 space-y-3 text-sm text-gray-300 border-t border-gray-700 pt-3">
                              <div>
                                <p>
                                  <span className="font-semibold">
                                    About:
                                  </span>{" "}
                                  {event.about}
                                </p>
                                <p>
                                  <span className="font-semibold">
                                    Learning:
                                  </span>{" "}
                                  {event.learning}
                                </p>
                              </div>

                              {/* Feedback box */}
                              <div>
                                <label className="block text-xs text-gray-400 mb-1">
                                  Your Feedback
                                </label>
                                <textarea
                                  rows={2}
                                  placeholder="Share your experience..."
                                  value={
                                    feedbackInputs[event.id || event._id] ||
                                    ""
                                  }
                                  onChange={(e) =>
                                    setFeedbackInputs((prev) => ({
                                      ...prev,
                                      [event.id || event._id]:
                                        e.target.value,
                                    }))
                                  }
                                  className="w-full bg-gray-900 border border-gray-700 rounded-lg px-3 py-2 text-xs text-white focus:outline-none focus:ring-2 focus:ring-blue-500"
                                />
                                <div className="flex justify-between items-center mt-2">
                                  <button
                                    onClick={() =>
                                      submitFeedback(
                                        event.id || event._id
                                      )
                                    }
                                    className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1.5 rounded text-xs"
                                  >
                                    Submit
                                  </button>
                                  <button
                                    onClick={() =>
                                      loadFeedbackForEvent(
                                        event.id || event._id
                                      )
                                    }
                                    className="text-xs text-blue-300 underline hover:text-blue-200"
                                  >
                                    {loadingFeedback[
                                      event.id || event._id
                                    ]
                                      ? "Loading..."
                                      : "View all feedback"}
                                  </button>
                                </div>
                              </div>

                              {/* Feedback list */}
                              {feedbackList[event.id || event._id] &&
                                feedbackList[event.id || event._id]
                                  .length > 0 && (
                                  <div className="border-t border-gray-700 pt-2 max-h-32 overflow-y-auto">
                                    <p className="text-xs text-gray-400 mb-1">
                                      Feedback:
                                    </p>
                                    {feedbackList[
                                      event.id || event._id
                                    ].map((fb) => (
                                      <div
                                        key={fb._id}
                                        className="mb-2"
                                      >
                                        <p className="text-xs text-blue-300 font-semibold">
                                          {fb.userName || "User"}
                                        </p>
                                        <p className="text-xs text-gray-200">
                                          {fb.feedback}
                                        </p>
                                      </div>
                                    ))}
                                  </div>
                                )}
                            </div>
                          )}
                        </div>
                      )
                    )}
                  </div>
                )}
              </section>
            </>
          )}

          {/* Registered tab */}
          {activeTab === "registered" && (
            <section id="registered">
              <h3 className="text-2xl font-bold mb-6 text-blue-400">
                Registered Events
              </h3>
              {registeredEvents.length === 0 ? (
                <p className="text-gray-400">
                  You have not registered for any events yet.
                </p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {registeredEvents.map((event) => (
                    <div
                      key={event._id}
                      className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg"
                    >
                      <h4 className="text-lg font-semibold text-white mb-2">
                        {event.name}
                      </h4>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.date}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.venue}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.shortDesc}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </section>
          )}

          {/* Attended tab */}
          {activeTab === "attended" && (
            <section id="attended">
              <h3 className="text-2xl font-bold mb-6 text-green-400">
                Attended Events
              </h3>
              {attendedEvents.length === 0 ? (
                <p className="text-gray-400">
                  You have not attended any events yet.
                </p>
              ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
                  {attendedEvents.map((event) => (
                    <div
                      key={event._id}
                      className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg"
                    >
                      <h4 className="text-lg font-semibold text-white mb-2">
                        {event.name}
                      </h4>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.date}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.venue}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                        {event.shortDesc}
                      </p>
                    </div>
                  ))}
                </div>
              )}
            </section>
          )}

          {/* Notifications panel */}
          {notificationsData.length > 0 && (
            <section className="mb-12">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-blue-400">
                  Notifications
                </h3>
                <button
                  onClick={handleMarkAllRead}
                  className="text-xs text-gray-300 underline hover:text-white"
                >
                  Mark all as read
                </button>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {notificationsData.map((n) => (
                  <div
                    key={n._id}
                    className={`bg-gray-900/80 border rounded-xl p-4 text-sm ${
                      n.isRead
                        ? "border-gray-700"
                        : "border-blue-600"
                    }`}
                  >
                    <div className="flex justify-between items-start mb-2">
                      <div>
                        <h4 className="text-white font-semibold">
                          {n.title}
                        </h4>
                        <p className="text-gray-300 text-xs mt-1">
                          {n.message}
                        </p>
                      </div>
                      {!n.isRead && (
                        <button
                          onClick={() =>
                            handleMarkOneRead(n._id)
                          }
                          className="text-xs text-blue-300 underline hover:text-blue-100"
                        >
                          Mark read
                        </button>
                      )}
                    </div>
                    <p className="text-[11px] text-gray-500">
                      {n.createdAt &&
                        new Date(
                          n.createdAt
                        ).toLocaleString()}
                    </p>
                  </div>
                ))}
              </div>
            </section>
          )}
        </section>
      </main>

      {/* Confirm registration modal */}
      {showConfirm && confirmEvent && (
        <div className="fixed inset-0 z-40 flex items-center justify-center bg-black/60">
          <div className="bg-gray-900 border border-gray-700 rounded-2xl p-6 max-w-md w-full">
            <h3 className="text-lg font-semibold text-white mb-3">
              Confirm Registration
            </h3>
            <p className="text-sm text-gray-300 mb-4">
              Do you want to register for{" "}
              <span className="font-semibold">
                {confirmEvent.name}
              </span>
              ?
            </p>
            <div className="flex justify-end gap-3">
              <button
                onClick={() => {
                  setShowConfirm(false);
                  setConfirmEvent(null);
                }}
                className="px-4 py-2 rounded-lg bg-gray-700 hover:bg-gray-600 text-sm"
              >
                Cancel
              </button>
              <button
                onClick={handleConfirmRegister}
                className="px-4 py-2 rounded-lg bg-blue-600 hover:bg-blue-700 text-sm font-semibold"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
