import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

/* ---------------- CALENDAR COMPONENT ---------------- */
function CompactEventCalendar({ events = [] }) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  const daysInMonth = new Date(year, month + 1, 0).getDate();
  const startingDay = new Date(year, month, 1).getDay();

  const monthNames = ["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];
  const days = [];

  for (let i = 0; i < startingDay; i++) days.push(<div key={i}></div>);
  for (let d = 1; d <= daysInMonth; d++) {
    days.push(
      <div key={d} className="text-center text-sm text-gray-300">
        {d}
      </div>
    );
  }

  return (
    <div className="bg-gray-800 border border-gray-700 rounded-xl p-4 w-64">
      <h4 className="text-center text-white font-semibold mb-2">
        {monthNames[month]} {year}
      </h4>
      <div className="grid grid-cols-7 gap-1">{days}</div>
    </div>
  );
}

/* ---------------- USER DASHBOARD ---------------- */
function UserDashboard() {
  const [events, setEvents] = useState([]);
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [confirmEvent, setConfirmEvent] = useState(null);

  const [searchName, setSearchName] = useState("");
  const [searchVenue, setSearchVenue] = useState("");
  const [dateFilter, setDateFilter] = useState("");

  useEffect(() => {
    axios.get(base_url + "events").then((res) => {
      setEvents(res.data.events || []);
    });
  }, []);

  const handleRegister = (event) => {
    if (!registeredEvents.find((e) => e._id === event._id)) {
      setRegisteredEvents([...registeredEvents, event]);
    }
  };

  const filteredEvents = events.filter((e) => {
    return (
      (!dateFilter || e.date === dateFilter) &&
      e.name.toLowerCase().includes(searchName.toLowerCase()) &&
      e.venue.toLowerCase().includes(searchVenue.toLowerCase())
    );
  });

  const logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-black text-white">

      {/* NAVBAR */}
      <nav className="bg-gray-950 px-8 py-4 flex justify-between items-center border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">Event Coordination Engine</h1>
        <div className="flex gap-6">
          <a href="#browse" className="hover:text-blue-400">Browse Events</a>
          <a href="#registered" className="hover:text-blue-400">My Registered Events</a>
          <button onClick={logout} className="hover:text-red-400">Logout</button>
        </div>
      </nav>

      {/* CALENDAR FIXED TOP-LEFT */}
      <div className="fixed top-24 left-6 z-40">
        <CompactEventCalendar events={events} />
      </div>

      {/* FILTERS */}
      <div className="px-10 mt-8">
        <div className="flex gap-4 mb-6">
          <input
            type="text"
            placeholder="Search by Event Name"
            value={searchName}
            onChange={(e) => setSearchName(e.target.value)}
            className="bg-gray-800 border border-gray-600 p-2 rounded"
          />
          <input
            type="text"
            placeholder="Search by Venue"
            value={searchVenue}
            onChange={(e) => setSearchVenue(e.target.value)}
            className="bg-gray-800 border border-gray-600 p-2 rounded"
          />
          <input
            type="date"
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value)}
            className="bg-gray-800 border border-gray-600 p-2 rounded"
          />
        </div>
      </div>

      {/* UPCOMING EVENTS */}
      <section id="browse" className="px-10">
        <h2 className="text-2xl font-bold text-blue-400 mb-6">Upcoming Events</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {filteredEvents.map((event) => (
            <div key={event._id} className="bg-gray-800 border border-gray-700 p-5 rounded-xl">
              <h3 className="font-semibold text-lg">{event.name}</h3>
              <p className="text-gray-400 text-sm">üìÖ {event.date}</p>
              <p className="text-gray-400 text-sm">üìç {event.venue}</p>
              <p className="text-gray-400 text-sm">{event.shortDesc}</p>
              <button
                onClick={() => setConfirmEvent(event)}
                className="mt-3 bg-blue-600 px-4 py-2 rounded hover:bg-blue-700"
              >
                Register
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* REGISTERED EVENTS */}
      <section id="registered" className="px-10 mt-14">
        <h2 className="text-2xl font-bold text-blue-400 mb-6">My Registered Events</h2>
        {registeredEvents.length === 0 ? (
          <p className="text-gray-400">No registered events yet.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            {registeredEvents.map((event) => (
              <div key={event._id} className="bg-gray-800 border border-green-600 p-5 rounded-xl">
                <h3 className="font-semibold">{event.name}</h3>
                <p className="text-gray-400 text-sm">üìÖ {event.date}</p>
                <p className="text-gray-400 text-sm">üìç {event.venue}</p>
                <p className="text-green-400 text-sm mt-2">‚úî Registered</p>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* CONFIRMATION MODAL */}
      {confirmEvent && (
        <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
          <div className="bg-gray-900 border border-gray-600 p-6 rounded-xl w-[90%] max-w-lg">
            <h3 className="text-xl font-bold text-blue-400 mb-4">
              Confirm Registration
            </h3>
            <p><b>Event:</b> {confirmEvent.name}</p>
            <p><b>Date:</b> {confirmEvent.date}</p>
            <p><b>Venue:</b> {confirmEvent.venue}</p>
            <p><b>Description:</b> {confirmEvent.shortDesc}</p>
            <div className="flex justify-end gap-4 mt-6">
              <button
                onClick={() => setConfirmEvent(null)}
                className="bg-gray-600 px-4 py-2 rounded"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  handleRegister(confirmEvent);
                  setConfirmEvent(null);
                }}
                className="bg-green-600 px-4 py-2 rounded"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}

    </div>
  );
}

export default UserDashboard;
