import axios from "axios";
import { useEffect, useState } from "react";
import { base_url } from "../components/baseUrl";

function OrganizerDashboard() {
  const [activeTab, setActiveTab] = useState("dashboard"); // "dashboard" | "events" | "registrations" | "attendance"

  const [myEvents, setMyEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const [registrations, setRegistrations] = useState([]);
  const [loadingRegistrations, setLoadingRegistrations] = useState(false);

  const [attendanceEvent, setAttendanceEvent] = useState(null);
  const [attendanceRegistrations, setAttendanceRegistrations] = useState([]);
  const [loadingAttendance, setLoadingAttendance] = useState(false);

  const [deleteModal, setDeleteModal] = useState({
    open: false,
    eventId: null,
  });

  const [name, setName] = useState("");
  const [eventDate, setEventDate] = useState("");
  const [venue, setVenue] = useState("");
  const [strength, setStrength] = useState("");
  const [shortDesc, setShortDesc] = useState("");
  const [about, setAbout] = useState("");
  const [learning, setLearning] = useState("");
  const [editEventId, setEditEventId] = useState(null);

  const navItemClass = (tab) =>
    `transition ${
      activeTab === tab
        ? "text-blue-300 underline font-semibold"
        : "text-gray-300 hover:text-blue-300"
    }`;

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      window.location.href = "/";
      return;
    }
    fetchMyEvents();
  }, []);

  const fetchMyEvents = async () => {
    try {
      const res = await axios.get(base_url + "events", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const allEvents = res.data.events || res.data || [];
      // Filter events created by this organizer (by email in createdBy)
      const email = getEmailFromToken();
      const mine = allEvents.filter(
        (e) => e.createdBy?.email === email
      );
      setMyEvents(mine);
    } catch (err) {
      console.error(err);
      alert("Failed to fetch events");
    }
  };

  const getEmailFromToken = () => {
    try {
      if (!token) return null;
      const payload = JSON.parse(atob(token.split(".")[1]));
      return payload.email;
    } catch {
      return null;
    }
  };

  const resetForm = () => {
    setEditEventId(null);
    setName("");
    setEventDate("");
    setVenue("");
    setStrength("");
    setShortDesc("");
    setAbout("");
    setLearning("");
  };

  const handleSaveEvent = async (e) => {
    e.preventDefault();

    if (
      !name ||
      !eventDate ||
      !venue ||
      !strength ||
      !shortDesc ||
      !about ||
      !learning
    ) {
      alert("Please fill all fields");
      return;
    }

    const eventData = {
      name,
      date: eventDate,
      venue,
      strength: parseInt(strength),
      shortDesc,
      about,
      learning,
    };

    try {
      if (editEventId) {
        await axios.put(
          base_url + `events/${editEventId}`,
          eventData,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        alert("Event updated and submitted for re-approval!");
      } else {
        await axios.post(base_url + "events", eventData, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert("Event submitted for admin approval!");
      }
      resetForm();
      fetchMyEvents();
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message || "Failed to save event"
      );
    }
  };

  const handleEditClick = (event) => {
    setEditEventId(event._id);
    setName(event.name);
    setEventDate(
      typeof event.date === "string"
        ? event.date.slice(0, 10)
        : new Date(event.date).toISOString().slice(0, 10)
    );
    setVenue(event.venue);
    setStrength(event.strength);
    setShortDesc(event.shortDesc);
    setAbout(event.about);
    setLearning(event.learning);
    setActiveTab("events");
  };

  const handleDeleteRequest = (eventId) => {
    setDeleteModal({ open: true, eventId });
  };

  const handleDeleteCancel = () => {
    setDeleteModal({ open: false, eventId: null });
  };

  const handleDeleteConfirmed = async () => {
    if (!deleteModal.eventId) return;
    try {
      await axios.delete(
        base_url + `events/${deleteModal.eventId}`,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert("Event deleted successfully");
      setDeleteModal({ open: false, eventId: null });
      fetchMyEvents();
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message || "Failed to delete event"
      );
    }
  };

  const fetchRegistrations = async (event) => {
    setSelectedEvent(event);
    setActiveTab("registrations");
    setLoadingRegistrations(true);
    setRegistrations([]);
    try {
      const res = await axios.get(
        `${base_url}events/${event._id}/registrations`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setRegistrations(res.data.registrations || []);
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message ||
          "Failed to load registrations for this event"
      );
    } finally {
      setLoadingRegistrations(false);
    }
  };

  const openAttendance = async (event) => {
    setAttendanceEvent(event);
    setActiveTab("attendance");
    setLoadingAttendance(true);
    setAttendanceRegistrations([]);
    try {
      const res = await axios.get(
        `${base_url}events/${event._id}/registrations`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setAttendanceRegistrations(res.data.registrations || []);
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message ||
          "Failed to load registrations for this event"
      );
    } finally {
      setLoadingAttendance(false);
    }
  };

  const markAttendance = async (userId, status) => {
    if (!attendanceEvent) return;
    try {
      await axios.post(
        `${base_url}events/${attendanceEvent._id}/attendance`,
        { userId, status },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setAttendanceRegistrations((prev) =>
        prev.map((r) =>
          r.userId === userId ? { ...r, attendanceStatus: status } : r
        )
      );
    } catch (err) {
      console.error(err);
      alert(
        err.response?.data?.message || "Failed to update attendance"
      );
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  const canMarkAttendance =
    attendanceEvent &&
    new Date(attendanceEvent.date) <= new Date();

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-8 py-4 flex justify-between items-center shadow-lg border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">
          Organizer Dashboard
        </h1>

        <div className="flex space-x-8 text-lg">
          <button
            onClick={() => setActiveTab("dashboard")}
            className={navItemClass("dashboard")}
          >
            Overview
          </button>

          <button
            onClick={() => {
              setActiveTab("events");
              resetForm();
            }}
            className={navItemClass("events")}
          >
            My Events
          </button>

          <button
            onClick={() => {
              if (selectedEvent) {
                setActiveTab("registrations");
              } else {
                alert("Select an event to view registrations");
              }
            }}
            className={navItemClass("registrations")}
          >
            Registrations
          </button>

          <button
            onClick={() => {
              if (attendanceEvent) {
                setActiveTab("attendance");
              } else {
                alert("Open attendance from an event first");
              }
            }}
            className={navItemClass("attendance")}
          >
            Attendance
          </button>

          <button
            onClick={handleLogout}
            className="text-red-400 hover:text-red-500 transition"
          >
            Logout
          </button>
        </div>
      </nav>

      {/* MAIN CONTENT */}
      <div className="px-8 py-8">
        {/* DASHBOARD / OVERVIEW */}
        {activeTab === "dashboard" && (
          <section>
            <h2 className="text-3xl font-bold text-blue-400 mb-4">
              Overview
            </h2>
            <p className="text-gray-300 mb-6">
              Manage your events, view registrations, and mark attendance.
            </p>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-gray-800/80 border border-gray-700 p-4 rounded-2xl shadow">
                <h3 className="text-lg font-semibold mb-2">
                  Total Events
                </h3>
                <p className="text-3xl font-bold text-blue-400">
                  {myEvents.length}
                </p>
              </div>

              <div className="bg-gray-800/80 border border-gray-700 p-4 rounded-2xl shadow">
                <h3 className="text-lg font-semibold mb-2">
                  Upcoming Events
                </h3>
                <p className="text-3xl font-bold text-green-400">
                  {
                    myEvents.filter(
                      (e) => new Date(e.date) >= new Date()
                    ).length
                  }
                </p>
              </div>

              <div className="bg-gray-800/80 border border-gray-700 p-4 rounded-2xl shadow">
                <h3 className="text-lg font-semibold mb-2">
                  Past Events
                </h3>
                <p className="text-3xl font-bold text-yellow-400">
                  {
                    myEvents.filter(
                      (e) => new Date(e.date) < new Date()
                    ).length
                  }
                </p>
              </div>
            </div>
          </section>
        )}

        {/* EVENTS TAB: LIST + CREATE / EDIT */}
        {activeTab === "events" && (
          <section className="space-y-8">
            {/* Form */}
            <div className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg">
              <h2 className="text-2xl font-bold text-blue-400 mb-4">
                {editEventId ? "Edit Event" : "Create Event"}
              </h2>
              <p className="text-gray-400 text-sm mb-4">
                Organizer events are submitted to admin for approval.
              </p>

              <form
                onSubmit={handleSaveEvent}
                className="space-y-4"
              >
                <input
                  type="text"
                  placeholder="Event Name"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white focus:border-blue-500 focus:outline-none"
                />
                <input
                  type="date"
                  value={eventDate}
                  onChange={(e) => setEventDate(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white focus:border-blue-500 focus:outline-none"
                />
                <input
                  type="text"
                  placeholder="Venue"
                  value={venue}
                  onChange={(e) => setVenue(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white focus:border-blue-500 focus:outline-none"
                />
                <input
                  type="number"
                  placeholder="Expected Participants"
                  value={strength}
                  onChange={(e) => setStrength(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white focus:border-blue-500 focus:outline-none"
                />
                <textarea
                  placeholder="Short Description"
                  value={shortDesc}
                  onChange={(e) => setShortDesc(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white h-20 resize-none focus:border-blue-500 focus:outline-none"
                />
                <textarea
                  placeholder="About Event"
                  value={about}
                  onChange={(e) => setAbout(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white h-24 resize-none focus:border-blue-500 focus:outline-none"
                />
                <textarea
                  placeholder="Learning Outcomes"
                  value={learning}
                  onChange={(e) => setLearning(e.target.value)}
                  className="w-full p-3 rounded bg-gray-900 border border-gray-600 text-white h-24 resize-none focus:border-blue-500 focus:outline-none"
                />

                <div className="flex justify-end gap-4 mt-2">
                  {editEventId && (
                    <button
                      type="button"
                      onClick={resetForm}
                      className="bg-gray-600 hover:bg-gray-700 px-6 py-2 rounded transition"
                    >
                      Cancel
                    </button>
                  )}
                  <button
                    type="submit"
                    className="bg-green-600 hover:bg-green-700 px-6 py-2 rounded transition font-semibold"
                  >
                    {editEventId ? "Update Event" : "Create Event"}
                  </button>
                </div>
              </form>
            </div>

            {/* Events list */}
            <div>
              <h2 className="text-2xl font-bold text-blue-400 mb-4">
                My Events
              </h2>

              {myEvents.length === 0 ? (
                <p className="text-gray-400">
                  You have not created any events yet.
                </p>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {myEvents.map((event) => (
                    <div
                      key={event._id}
                      className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-400/20 transition-all"
                    >
                      <h3 className="text-xl font-semibold text-white mb-2">
                        {event.name}
                      </h3>
                      <p className="text-gray-400 text-sm mb-1">
                         {event.date}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                         {event.venue}
                      </p>
                      <p className="text-gray-400 text-sm mb-1">
                         {event.strength} participants
                      </p>
                      <p className="text-gray-400 text-sm mb-3">
                         {event.shortDesc}
                      </p>
                      <p className="text-xs text-yellow-400 mb-3">
                        {event.approved
                          ? "Approved & Published"
                          : "Pending Admin Approval"}
                      </p>

                      <div className="flex flex-wrap gap-2">
                        <button
                          onClick={() => handleEditClick(event)}
                          className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
                        >
                          Edit
                        </button>

                        <button
                          onClick={() => fetchRegistrations(event)}
                          className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-1 rounded text-sm"
                        >
                          View Registrations
                        </button>

                        <button
                          onClick={() => openAttendance(event)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm"
                        >
                          Mark Attendance
                        </button>

                        <button
                          onClick={() => handleDeleteRequest(event._id)}
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-sm"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </section>
        )}

        {/* REGISTRATIONS TAB */}
        {activeTab === "registrations" && (
          <section>
            <h2 className="text-2xl font-bold text-blue-400 mb-4">
              Registrations
            </h2>

            {!selectedEvent ? (
              <p className="text-gray-400">
                Select an event from "My Events" to view registrations.
              </p>
            ) : (
              <>
                <p className="text-gray-300 mb-4">
                  Event:{" "}
                  <span className="font-semibold">
                    {selectedEvent.name}
                  </span>
                </p>

                {loadingRegistrations ? (
                  <p className="text-gray-400">
                    Loading registrations...
                  </p>
                ) : registrations.length === 0 ? (
                  <p className="text-gray-400">
                    No registrations for this event yet.
                  </p>
                ) : (
                  <div className="overflow-x-auto bg-gray-800/80 border border-gray-700 rounded-2xl">
                    <table className="min-w-full text-sm">
                      <thead className="bg-gray-900/80">
                        <tr>
                          <th className="px-4 py-2 text-left text-gray-300">
                            Name
                          </th>
                          <th className="px-4 py-2 text-left text-gray-300">
                            Email
                          </th>
                          <th className="px-4 py-2 text-left text-gray-300">
                            Branch
                          </th>
                          <th className="px-4 py-2 text-left text-gray-300">
                            Roll No.
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {registrations.map((r) => (
                          <tr
                            key={r.userId}
                            className="border-t border-gray-800"
                          >
                            <td className="px-4 py-2 text-gray-100">
                              {r.fullName}
                            </td>
                            <td className="px-4 py-2 text-gray-300">
                              {r.email}
                            </td>
                            <td className="px-4 py-2 text-gray-300">
                              {r.branch || "-"}
                            </td>
                            <td className="px-4 py-2 text-gray-300">
                              {r.rollNumber || "-"}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </>
            )}
          </section>
        )}

        {/* ATTENDANCE TAB */}
        {activeTab === "attendance" && (
          <section>
            <h2 className="text-2xl font-bold text-blue-400 mb-4">
              Attendance
            </h2>

            {!attendanceEvent ? (
              <p className="text-gray-400">
                Open attendance from "My Events" to mark attendance.
              </p>
            ) : (
              <>
                <p className="text-gray-300 mb-4">
                  Event:{" "}
                  <span className="font-semibold">
                    {attendanceEvent.name}
                  </span>{" "}
                  ({attendanceEvent.date})
                </p>

                {!canMarkAttendance && (
                  <p className="text-yellow-400 mb-4 text-sm">
                    You can only mark attendance on or after the event
                    date.
                  </p>
                )}

                {loadingAttendance ? (
                  <p className="text-gray-400">
                    Loading registrations...
                  </p>
                ) : attendanceRegistrations.length === 0 ? (
                  <p className="text-gray-400">
                    No registrations to mark attendance.
                  </p>
                ) : (
                  <div className="overflow-x-auto bg-gray-800/80 border border-gray-700 rounded-2xl">
                    <table className="min-w-full text-sm">
                      <thead className="bg-gray-900/80">
                        <tr>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Name
                          </th>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Email
                          </th>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Branch
                          </th>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Roll No.
                          </th>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Status
                          </th>
                          <th className="px-2 py-2 text-left text-gray-300">
                            Actions
                          </th>
                        </tr>
                      </thead>
                      <tbody>
                        {attendanceRegistrations.map((r) => (
                          <tr
                            key={r.userId}
                            className="border-t border-gray-800"
                          >
                            <td className="px-2 py-1 text-gray-100">
                              {r.fullName}
                            </td>
                            <td className="px-2 py-1 text-gray-300">
                              {r.email}
                            </td>
                            <td className="px-2 py-1 text-gray-300">
                              {r.branch || "-"}
                            </td>
                            <td className="px-2 py-1 text-gray-300">
                              {r.rollNumber || "-"}
                            </td>
                            <td className="px-2 py-1">
                              {r.attendanceStatus === "present" && (
                                <span className="text-green-400 font-semibold">
                                  Present
                                </span>
                              )}
                              {r.attendanceStatus === "absent" && (
                                <span className="text-red-400 font-semibold">
                                  Absent
                                </span>
                              )}
                              {r.attendanceStatus === "not_marked" && (
                                <span className="text-gray-400">
                                  Not marked
                                </span>
                              )}
                            </td>
                            <td className="px-2 py-1">
                              <div className="flex gap-1">
                                <button
                                  disabled={!canMarkAttendance}
                                  onClick={() =>
                                    markAttendance(
                                      r.userId,
                                      "present"
                                    )
                                  }
                                  className={`bg-green-600 hover:bg-green-700 text-white px-2 py-0.5 rounded ${
                                    !canMarkAttendance
                                      ? "opacity-50 cursor-not-allowed"
                                      : ""
                                  }`}
                                >
                                  Present
                                </button>
                                <button
                                  disabled={!canMarkAttendance}
                                  onClick={() =>
                                    markAttendance(
                                      r.userId,
                                      "absent"
                                    )
                                  }
                                  className={`bg-red-600 hover:bg-red-700 text-white px-2 py-0.5 rounded ${
                                    !canMarkAttendance
                                      ? "opacity-50 cursor-not-allowed"
                                      : ""
                                  }`}
                                >
                                  Absent
                                </button>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </>
            )}
          </section>
        )}
      </div>

      {/* Delete Confirmation Modal */}
      {deleteModal.open && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-2xl shadow-lg w-80 text-white">
            <h3 className="text-xl font-bold mb-4 text-red-500">
              Confirm Delete
            </h3>
            <p className="mb-6">
              Are you sure you want to delete this event?
            </p>
            <div className="flex justify-end gap-4">
              <button
                onClick={handleDeleteCancel}
                className="bg-gray-600 hover:bg-gray-700 px-4 py-2 rounded"
              >
                Cancel
              </button>
              <button
                onClick={handleDeleteConfirmed}
                className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded"
              >
                Delete
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default OrganizerDashboard;
