// src/pages/OrganizerDashboard.js
import { useState } from "react";

function OrganizerDashboard() {
  const initialEvents = [
    { id: 1, title: "React Workshop", date: "2025-09-10", category: "Technology", venue: "Hall A", strength: 10, shortDesc: "Learn React basics", about: "Deep dive into React basics and hooks, projects, and best practices.", image: "/assets/react.jpg" },
    { id: 2, title: "AI Seminar", date: "2025-09-15", category: "Technology", venue: "Hall B", strength: 5, shortDesc: "Intro to AI concepts", about: "Learn about AI concepts, machine learning, and real-world applications.", image: "/assets/ai.jpg" },
    { id: 3, title: "Web Dev Meetup", date: "2025-09-20", category: "Technology", venue: "Hall C", strength: 8, shortDesc: "Networking and projects", about: "Collaborate with peers, work on projects, and improve your web development skills.", image: "/assets/web.jpg" },
  ];

  const [events, setEvents] = useState(initialEvents);
  const [pastEvents] = useState([
    { id: 4, title: "Python Bootcamp", date: "2025-08-12", category: "Technology", venue: "Hall D", image: "/assets/python.jpg" },
    { id: 5, title: "Data Science Workshop", date: "2025-07-20", category: "Technology", venue: "Hall E", image: "/assets/ds.jpg" },
  ]);
  const [expandedEventId, setExpandedEventId] = useState(null);
  const [editingEvent, setEditingEvent] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newEvent, setNewEvent] = useState({ title: "", date: "", category: "", venue: "", strength: 0, shortDesc: "", about: "", image: "" });

  const toggleDetails = (id) => {
    setExpandedEventId(expandedEventId === id ? null : id);
  };

  const deleteEvent = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEvents(events.filter(ev => ev.id !== id));
    }
  };

  const handleEditEvent = (event) => setEditingEvent(event);

  const handleSaveEdit = () => {
    setEvents(events.map(ev => ev.id === editingEvent.id ? editingEvent : ev));
    setEditingEvent(null);
  };

  const handleCreateEvent = () => {
    const newId = events.length + pastEvents.length + 1;
    setEvents([...events, { ...newEvent, id: newId }]);
    setNewEvent({ title: "", date: "", category: "", venue: "", strength: 0, shortDesc: "", about: "", image: "" });
    setShowCreateForm(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white px-6 py-8">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-6 py-4 flex justify-between items-center shadow-lg border-b border-gray-700 mb-6">
        <h1 className="text-2xl font-bold text-blue-400">Organizer Dashboard</h1>
        <div className="flex space-x-6 text-lg">
          <a href="#upcoming" className="hover:text-blue-300 transition">Events</a>
          <a href="#past" className="hover:text-blue-300 transition">Past Events</a>
          <button onClick={() => alert("Logout")} className="hover:text-blue-400 transition">Logout</button>
        </div>
      </nav>

      {/* Create Event Button/Form */}
      <div className="mb-6">
        <button
          onClick={() => setShowCreateForm(!showCreateForm)}
          className="bg-green-600 text-white px-4 py-2 rounded-xl hover:bg-green-700 transition"
        >
          {showCreateForm ? "Cancel" : "Create New Event"}
        </button>

        {showCreateForm && (
          <div className="mt-4 bg-gray-800/80 backdrop-blur-md p-6 rounded-2xl shadow-lg">
            <h2 className="text-xl font-bold text-blue-400 mb-4">New Event Details</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <input type="text" placeholder="Title" value={newEvent.title} onChange={e => setNewEvent({ ...newEvent, title: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <input type="date" value={newEvent.date} onChange={e => setNewEvent({ ...newEvent, date: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <input type="text" placeholder="Category" value={newEvent.category} onChange={e => setNewEvent({ ...newEvent, category: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <input type="text" placeholder="Venue" value={newEvent.venue} onChange={e => setNewEvent({ ...newEvent, venue: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <input type="number" placeholder="Seats" value={newEvent.strength} onChange={e => setNewEvent({ ...newEvent, strength: Number(e.target.value) })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <input type="text" placeholder="Image URL" value={newEvent.image} onChange={e => setNewEvent({ ...newEvent, image: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <textarea placeholder="Short Description" value={newEvent.shortDesc} onChange={e => setNewEvent({ ...newEvent, shortDesc: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 col-span-2 focus:ring-2 focus:ring-blue-500 outline-none"/>
              <textarea placeholder="About" value={newEvent.about} onChange={e => setNewEvent({ ...newEvent, about: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 col-span-2 focus:ring-2 focus:ring-blue-500 outline-none"/>
            </div>
            <button onClick={handleCreateEvent} className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-xl transition">Create Event</button>
          </div>
        )}
      </div>

      <div className="flex flex-col md:flex-row gap-10">
        {/* Events List */}
        <div className="flex-1">
          {/* Upcoming Events */}
          <section id="upcoming" className="mb-12">
            <h2 className="text-2xl font-bold mb-6 text-blue-400">Your Events</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {events.map(ev => (
                <div key={ev.id} className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-500/30 hover:-translate-y-1 transition-all">
                  <img src={ev.image} alt={ev.title} className="w-full h-40 object-cover rounded-lg mb-3"/>
                  {editingEvent?.id === ev.id ? (
                    <div className="space-y-2">
                      <input type="text" value={editingEvent.title} onChange={e => setEditingEvent({ ...editingEvent, title: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <input type="date" value={editingEvent.date} onChange={e => setEditingEvent({ ...editingEvent, date: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <input type="text" value={editingEvent.venue} onChange={e => setEditingEvent({ ...editingEvent, venue: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <input type="text" value={editingEvent.category} onChange={e => setEditingEvent({ ...editingEvent, category: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <input type="number" value={editingEvent.strength} onChange={e => setEditingEvent({ ...editingEvent, strength: Number(e.target.value) })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <textarea value={editingEvent.shortDesc} onChange={e => setEditingEvent({ ...editingEvent, shortDesc: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <textarea value={editingEvent.about} onChange={e => setEditingEvent({ ...editingEvent, about: e.target.value })} className="p-2 rounded bg-gray-900 text-white border border-gray-600 w-full"/>
                      <button onClick={handleSaveEdit} className="bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded text-white w-full mt-2">Save</button>
                    </div>
                  ) : (
                    <>
                      <h3 className="text-lg font-semibold text-white">{ev.title}</h3>
                      <p className="text-gray-400 text-sm mb-1">üìÖ {ev.date}</p>
                      <p className="text-gray-400 text-sm mb-1">üìç {ev.venue}</p>
                      <p className="text-gray-400 text-sm mb-1">üè∑Ô∏è {ev.category}</p>
                      <p className="text-gray-400 text-sm mb-4">Seats: {ev.strength}</p>
                      <div className="flex gap-2">
                        <button onClick={() => toggleDetails(ev.id)} className="bg-blue-600 hover:bg-blue-700 px-3 py-1 rounded text-white">View</button>
                        <button onClick={() => handleEditEvent(ev)} className="bg-yellow-500 hover:bg-yellow-600 px-3 py-1 rounded text-white">Edit</button>
                        <button onClick={() => deleteEvent(ev.id)} className="bg-red-600 hover:bg-red-700 px-3 py-1 rounded text-white">Delete</button>
                      </div>
                      {expandedEventId === ev.id && (
                        <div className="mt-3 text-gray-300 border-t pt-2 text-sm">
                          <p>{ev.about}</p>
                        </div>
                      )}
                    </>
                  )}
                </div>
              ))}
            </div>
          </section>

          {/* Past Events */}
          <section id="past">
            <h2 className="text-2xl font-bold mb-6 text-blue-400">Past Events</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {pastEvents.map(ev => (
                <div key={ev.id} className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-green-500/30 hover:-translate-y-1 transition-all">
                  <img src={ev.image} alt={ev.title} className="w-full h-40 object-cover rounded-lg mb-3"/>
                  <h3 className="text-lg font-semibold text-white">{ev.title}</h3>
                  <p className="text-gray-400 text-sm mb-1">üìÖ {ev.date}</p>
                  <p className="text-gray-400 text-sm mb-1">üìç {ev.venue}</p>
                  <p className="text-gray-400 text-sm mb-1">üè∑Ô∏è {ev.category}</p>
                </div>
              ))}
            </div>
          </section>
        </div>
      </div>
    </div>
  );
}

export default OrganizerDashboard;
