import { useRef } from "react";
import Hero from "../components/Hero";
import EventCard from "../components/EventCard"; // example

function Home() {
  const eventsRef = useRef(null);

  const scrollToEvents = () => {
    if (eventsRef.current) {
      eventsRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div>
      {/* Hero with button */}
      <Hero scrollToEvents={scrollToEvents} />

      {/* Events Section */}
      <section ref={eventsRef} className="py-12 px-8 bg-gray-50">
        <h2 className="text-3xl font-bold mb-6 text-center">Upcoming Events</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Example EventCards */}
          <EventCard
            title="Tech Fest 2025"
            date="10 Nov 2025"
            image="/techfest.jpg"
            description="A celebration of innovation and creativity in technology."
          />
          <EventCard
            title="Pre-Placement Talk"
            date="15 Nov 2025"
            image="/preplacement.jpg"
            description="Career guidance & industry insights for students."
          />
        </div>
      </section>
    </div>
  );
}

export default Home;
