import { useState } from "react";

function AdminDashboard() {
  const [eventsData, setEventsData] = useState([]);
  const [search, setSearch] = useState("");
  const [date, setDate] = useState("");
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [showEventForm, setShowEventForm] = useState(false);
  const [editEvent, setEditEvent] = useState(null);

  const filteredEvents = eventsData.filter((event) => {
    return (
      event.name.toLowerCase().includes(search.toLowerCase()) &&
      (date ? event.date === date : true)
    );
  });

  const handleLogout = () => {
    window.location.href = "/home";
  };

  const handleDeleteEvent = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEventsData(eventsData.filter((ev) => ev._id !== id));
    }
  };

  const handleSaveEvent = async (newEvent) => {
    try {
      const response = await fetch("http://localhost:5000/events", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newEvent),
      });

      if (!response.ok) throw new Error("Failed to save event");

      const savedEvent = await response.json();
      setEventsData([...eventsData, { ...newEvent, _id: savedEvent.insertedId }]);
      alert("‚úÖ Event added successfully!");
      setEditEvent(null);
      setShowEventForm(false);
    } catch (error) {
      console.error("Error saving event:", error);
      alert("‚ùå Failed to save event.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Navbar */}
      <nav className="bg-gray-950 bg-opacity-80 backdrop-blur-md text-white px-8 py-4 flex justify-between items-center shadow-lg border-b border-gray-700">
        <h1 className="text-2xl font-bold text-blue-400">
          Event Coordination Engine
        </h1>
        <div className="flex space-x-8 text-lg">
          <a href="/home" className="hover:text-blue-300 transition">Home</a>
          <a href="#browse" className="hover:text-blue-300 transition">Browse Events</a>
          <a href="#profile" className="hover:text-blue-300 transition">Profile</a>
          <button onClick={handleLogout} className="hover:text-blue-400 transition">Logout</button>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="text-center py-12">
        <h2 className="text-4xl font-bold text-blue-400 tracking-wide">
          Welcome, <span className="text-white">Admin</span>
        </h2>
        <p className="text-gray-400 mt-3 text-lg">Manage all users and events</p>
        <button
          onClick={() => document.getElementById("browse").scrollIntoView({ behavior: "smooth" })}
          className="mt-6 bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-xl font-semibold shadow-lg transition"
        >
          Browse Events
        </button>
      </section>

      <div className="flex flex-col md:flex-row px-10 gap-10 pb-12">
        {/* Main Content */}
        <div className="flex-1">
          {/* Inline Event Form */}
          <div className="mb-6 bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-2xl font-bold text-blue-400 mb-4">
              {editEvent ? "Edit Event" : "Create Event"}
            </h3>
            <EventForm
              event={editEvent || {}}
              onSave={handleSaveEvent}
              onClose={() => setEditEvent(null)}
            />
          </div>

          {/* Upcoming Events */}
          <section id="browse" className="mb-12">
            <h3 className="text-2xl font-bold mb-6 text-blue-400">Upcoming Events</h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredEvents.map((event) => (
                <div
                  key={event._id}
                  className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-500/30 hover:-translate-y-1 transition-all"
                >
                  <h4 className="text-lg font-semibold text-white mb-2">{event.name}</h4>
                  <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
                  <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
                  <p className="text-gray-400 text-sm mb-4">üë• Strength: {event.strength}</p>
                  <p className="text-gray-300 text-sm mb-4">{event.shortDesc}</p>

                  {/* Buttons */}
                  <div className="flex gap-2">
                    <button
                      onClick={() => setSelectedEvent(event)}
                      className="flex-1 py-2 rounded-lg bg-gray-700 hover:bg-gray-600 text-white font-semibold transition"
                    >
                      View
                    </button>
                    <button
                      onClick={() => setEditEvent(event)}
                      className="flex-1 py-2 rounded-lg bg-yellow-600 hover:bg-yellow-700 text-white font-semibold transition"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteEvent(event._id)}
                      className="flex-1 py-2 rounded-lg bg-red-600 hover:bg-red-700 text-white font-semibold transition"
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>

        {/* Sidebar */}
        <div className="w-full md:w-1/3 space-y-8">
          {/* Search & Filter */}
          <div className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Search & Filter</h3>
            <input
              type="text"
              placeholder="Search by keyword..."
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <input
              type="date"
              className="w-full bg-gray-900 text-white border border-gray-600 p-2 rounded mb-3 focus:ring-2 focus:ring-blue-500 outline-none"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
            <button
              onClick={() => { setSearch(""); setDate(""); }}
              className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition"
            >
              Clear Filters
            </button>
          </div>

          {/* Profile Section */}
          <div id="profile" className="bg-gray-800/80 backdrop-blur-md border border-gray-700 p-6 rounded-2xl shadow-lg">
            <h3 className="text-xl font-bold mb-4 text-blue-400">Profile</h3>
            <div className="flex items-center space-x-4 mb-4">
              <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center font-bold text-lg">A</div>
              <div>
                <p className="font-semibold text-white">Admin Name</p>
                <p className="text-gray-400 text-sm">admin@email.com</p>
              </div>
            </div>
            <button className="bg-blue-600 text-white w-full py-2 rounded hover:bg-blue-700 transition">Edit Profile</button>
          </div>
        </div>
      </div>
    </div>
  );
}

// EventForm Component
function EventForm({ event = {}, onSave, onClose }) {
  const [name, setName] = useState(event.name || "");
  const [date, setDate] = useState(event.date || "");
  const [venue, setVenue] = useState(event.venue || "");
  const [strength, setStrength] = useState(event.strength || "");
  const [shortDesc, setShortDesc] = useState(event.shortDesc || "");
  const [about, setAbout] = useState(event.about || "");
  const [learning, setLearning] = useState(event.learning || "");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !date || !venue || !strength || !shortDesc || !about || !learning) {
      alert("Please fill in all fields before saving.");
      return;
    }

    onSave({
      name,
      date,
      venue,
      strength: parseInt(strength),
      shortDesc,
      about,
      learning,
      createdAt: new Date(),
    });

    // Clear form if creating new event
    if (!event._id) {
      setName(""); setDate(""); setVenue(""); setStrength(""); setShortDesc(""); setAbout(""); setLearning("");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <input type="text" placeholder="Event Name" value={name} onChange={e => setName(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white"/>
      <input type="date" value={date} onChange={e => setDate(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white"/>
      <input type="text" placeholder="Venue" value={venue} onChange={e => setVenue(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white"/>
      <input type="number" placeholder="Strength" value={strength} onChange={e => setStrength(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white"/>
      <textarea placeholder="Short Description" value={shortDesc} onChange={e => setShortDesc(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-20 resize-none"/>
      <textarea placeholder="About Event" value={about} onChange={e => setAbout(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-24 resize-none"/>
      <textarea placeholder="Learning Outcomes" value={learning} onChange={e => setLearning(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600 text-white h-24 resize-none"/>
      <div className="flex justify-end gap-4 mt-2">
        {event._id && <button type="button" onClick={onClose} className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded">Cancel</button>}
        <button type="submit" className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded">{event._id ? "Update" : "Save"}</button>
      </div>
    </form>
  );
}

