import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios'

function Register() {
  const [fullName, setFullName] = useState("");
  const [rollNumber, setRollNumber] = useState("");
  const [branch, setBranch] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleRegister = (e) => {
    e.preventDefault();

    const newUser = { fullName, rollNumber, branch, email, password };
    axios.post("/register",newUser)
    .then((res)=>{
      if(res.status === "Success"){
        alert("User Registered")
      }
      else{
        alert("Error")
      }
    })
    .catch((err)=>{
      console.log(err)
    })
    
  };

  return (
    <div
      className="relative flex items-center justify-center min-h-screen bg-cover bg-center"
      style={{ backgroundImage: "url('/college-bg.jpg')" }} // same bg as login
    >
      {/* Blur overlay */}
      <div className="absolute inset-0 bg-black/40 backdrop-blur-sm"></div>

      {/* Register Form */}
      <form
        onSubmit={handleRegister}
        className="relative bg-white/90 shadow-2xl rounded-2xl p-8 w-96 backdrop-blur-md"
      >
        <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
          Register
        </h2>

        <input
          type="text"
          placeholder="Full Name"
          value={fullName}
          onChange={(e) => setFullName(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <input
          type="text"
          placeholder="Roll Number"
          value={rollNumber}
          onChange={(e) => setRollNumber(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <input
          type="text"
          placeholder="Branch"
          value={branch}
          onChange={(e) => setBranch(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <input
          type="email"
          placeholder="College Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <button
          type="submit"
          className="bg-indigo-600 text-white w-full py-2 rounded-lg hover:bg-indigo-700 transition"
        >
          Register
        </button>

        <p className="mt-4 text-center text-gray-700">
          Already have an account?{" "}
          <a href="/" className="text-indigo-600 font-semibold hover:underline">
            Login
          </a>
        </p>
      </form>
    </div>
  );
}

export default Register;
