import { useState } from "react";

function Dashboard({ role }) {
  const [eventsData, setEventsData] = useState([
    { id: 1, title: "Tech Talk 2025", date: "2025-10-25", category: "Technology", venue: "Online" },
    { id: 2, title: "Art Exhibition", date: "2025-11-10", category: "Culture", venue: "City Art Gallery" },
    { id: 3, title: "Music Concert", date: "2025-12-05", category: "Entertainment", venue: "Central Park" },
  ]);

  const [selectedEvent, setSelectedEvent] = useState(null);
  const [showEventForm, setShowEventForm] = useState(false);
  const [editEvent, setEditEvent] = useState(null);

  // Add or Edit Event
  const handleSaveEvent = (event) => {
    if (editEvent) {
      setEventsData(eventsData.map(ev => ev.id === editEvent.id ? { ...event, id: editEvent.id } : ev));
    } else {
      setEventsData([...eventsData, { ...event, id: Date.now() }]);
    }
    setEditEvent(null);
    setShowEventForm(false);
  };

  const handleDeleteEvent = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEventsData(eventsData.filter(ev => ev.id !== id));
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Hero Section */}
      <section className="text-center py-12">
        <h2 className="text-4xl font-bold text-blue-400 tracking-wide">
          Welcome, <span className="text-white">{role === "organizer" ? "Organizer" : role === "admin" ? "Admin" : "User"}</span>
        </h2>
      </section>

      <div className="px-10 pb-12">
        {/* Organizer Create Event Button */}
        {role === "organizer" && (
          <div className="mb-6 flex justify-end">
            <button
              onClick={() => setShowEventForm(true)}
              className="bg-green-600 hover:bg-green-700 px-6 py-2 rounded-xl font-semibold"
            >
              + Create Event
            </button>
          </div>
        )}

        {/* Event List */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {eventsData.map((event) => (
            <div key={event.id} className="bg-gray-800/80 border border-gray-700 p-6 rounded-2xl shadow-lg hover:shadow-blue-500/30 transition-all">
              <h4 className="text-lg font-semibold text-white mb-2">{event.title}</h4>
              <p className="text-gray-400 text-sm mb-1">üìÖ {event.date}</p>
              <p className="text-gray-400 text-sm mb-1">üìç {event.venue}</p>
              <p className="text-gray-400 text-sm mb-4">üè∑Ô∏è {event.category}</p>

              <div className="flex gap-2">
                <button
                  onClick={() => setSelectedEvent(event)}
                  className="flex-1 py-2 rounded-lg bg-gray-700 hover:bg-gray-600 text-white font-semibold transition"
                >
                  View Details
                </button>

                {/* Organizer Actions */}
                {role === "organizer" && (
                  <>
                    <button
                      onClick={() => { setEditEvent(event); setShowEventForm(true); }}
                      className="flex-1 py-2 rounded-lg bg-yellow-600 hover:bg-yellow-700 text-white font-semibold transition"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteEvent(event.id)}
                      className="flex-1 py-2 rounded-lg bg-red-600 hover:bg-red-700 text-white font-semibold transition"
                    >
                      Delete
                    </button>
                  </>
                )}
              </div>
            </div>
          ))}
        </div>

        {/* Event Form Modal */}
        {showEventForm && (
          <EventForm
            event={editEvent}
            onSave={handleSaveEvent}
            onClose={() => { setEditEvent(null); setShowEventForm(false); }}
          />
        )}
      </div>
    </div>
  );
}

// Simple Event Form Component
function EventForm({ event = {}, onSave, onClose }) {
  const [title, setTitle] = useState(event.title || "");
  const [date, setDate] = useState(event.date || "");
  const [category, setCategory] = useState(event.category || "");
  const [venue, setVenue] = useState(event.venue || "");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ title, date, category, venue });
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
      <form onSubmit={handleSubmit} className="bg-gray-900 p-8 rounded-2xl w-full max-w-md space-y-4">
        <h3 className="text-2xl font-bold text-blue-400">{event.id ? "Edit Event" : "Create Event"}</h3>
        <input type="text" placeholder="Title" value={title} onChange={e => setTitle(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600"/>
        <input type="date" value={date} onChange={e => setDate(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600"/>
        <input type="text" placeholder="Category" value={category} onChange={e => setCategory(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600"/>
        <input type="text" placeholder="Venue" value={venue} onChange={e => setVenue(e.target.value)} className="w-full p-2 rounded bg-gray-800 border border-gray-600"/>
        <div className="flex justify-end gap-4 mt-2">
          <button type="button" onClick={onClose} className="bg-red-600 hover:bg-red-700 px-4 py-2 rounded">Cancel</button>
          <button type="submit" className="bg-green-600 hover:bg-green-700 px-4 py-2 rounded">Save</button>
        </div>
      </form>
    </div>
  );
}

export default Dashboard;
