import { useState } from "react";
import { useNavigate } from "react-router-dom";

function EventCard({ image, title, date, description, venue }) {
  const [showDetails, setShowDetails] = useState(false);
  const navigate = useNavigate();

  return (
    <div className="card shadow-sm mb-4">
      {/* Event Image */}
      <img src={image} className="card-img-top" alt={title} style={{ height: "160px", objectFit: "cover" }} />

      {/* Event Info */}
      <div className="card-body">
        <h5 className="card-title">{title}</h5>
        <p className="text-muted mb-3">{date}</p>

        {/* Buttons */}
        <div className="d-flex gap-2 mb-3">
          <button
            onClick={() => setShowDetails(!showDetails)}
            className="btn btn-primary flex-grow-1"
          >
            {showDetails ? "Hide" : "View"}
          </button>
          <button
            onClick={() => navigate("/login")}
            className="btn btn-success flex-grow-1"
          >
            Register
          </button>
        </div>

        {/* Toggle Details */}
        {showDetails && (
          <div className="border-top pt-3 small text-secondary">
            <p><strong>üìç Venue:</strong> {venue}</p>
            <p><strong>‚ÑπÔ∏è About:</strong> {description}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default EventCard;
