import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { base_url } from "../components/baseUrl"; // make sure base_url = "http://localhost:4000/"

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("user"); // default role
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // send login request to backend
      const res = await axios.post(`${base_url}login`, {
        email,
        password,
        role,
      });

      // handle success
      if (res.data.status === "Success") {
        alert(res.data.message);

        // store user data and token in localStorage
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("user", JSON.stringify(res.data.user));

        // navigate based on role
        if (res.data.user.role === "admin") {
          navigate("/admin-dashboard");
        } else if (res.data.user.role === "organizer") {
          navigate("/organizer-dashboard");
        } else {
          navigate("/user-dashboard");
        }
      } else {
        alert(res.data.message || "Login failed");
      }
    } catch (err) {
      console.error("Login error:", err);
      alert("Server error — please check backend connection");
    }
  };

  return (
    <div
      className="relative flex items-center justify-center min-h-screen bg-cover bg-center"
      style={{ backgroundImage: "url('/college-bg.jpg')" }}
    >
      {/* Blur overlay */}
      <div className="absolute inset-0 bg-black/40 backdrop-blur-sm"></div>

      {/* Login form */}
      <form
        onSubmit={handleLogin}
        className="relative bg-white/90 shadow-2xl rounded-2xl p-8 w-96 backdrop-blur-md"
      >
        <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
          Login
        </h2>

        <input
          type="email"
          placeholder="College Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        />

        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="border rounded-lg w-full px-3 py-2 mb-4 focus:ring-2 focus:ring-indigo-400 outline-none"
          required
        >
          <option value="user">User</option>
          <option value="organizer">Organizer</option>
          <option value="admin">Admin</option>
        </select>

        <button
          type="submit"
          className="bg-indigo-600 text-white w-full py-2 rounded-lg hover:bg-indigo-700 transition"
        >
          Login
        </button>

        <p className="mt-4 text-center text-gray-700">
          Don’t have an account?{" "}
          <a
            href="/register"
            className="text-indigo-600 font-semibold hover:underline"
          >
            Register
          </a>
        </p>
      </form>
    </div>
  );
}

export default Login;
