// src/pages/AdminDashboard.js
import React, { useState } from "react";

function AdminDashboard() {
  const [events, setEvents] = useState([
    { id: 1, name: "React Workshop", date: "2025-09-10", venue: "Hall A", strength: 10, shortDesc: "Learn React basics and hooks.", about: "Deep dive into React basics and hooks, projects, and best practices.", learning: "Understand components, state, hooks, and build small projects." },
    { id: 2, name: "AI Seminar", date: "2025-09-15", venue: "Hall B", strength: 5, shortDesc: "Intro to AI concepts.", about: "Learn about AI concepts, machine learning, and real-world applications.", learning: "Gain insight into AI, ML algorithms, and practical examples." },
  ]);

  const [users] = useState([
    { id: 1, name: "John Doe", email: "john@example.com" },
    { id: 2, name: "Jane Smith", email: "jane@example.com" },
  ]);

  const [organizers] = useState([
    { id: 1, name: "Organizer 1", email: "org1@example.com" },
    { id: 2, name: "Organizer 2", email: "org2@example.com" },
  ]);

  const [newEvent, setNewEvent] = useState({ name: "", date: "", venue: "", strength: 0, shortDesc: "", about: "", learning: "" });
  const [expandedEventId, setExpandedEventId] = useState(null);

  const handleAddEvent = () => {
    if (!newEvent.name || !newEvent.date || !newEvent.venue || newEvent.strength <= 0 || !newEvent.shortDesc || !newEvent.about || !newEvent.learning) {
      alert("Please fill all fields with valid values!");
      return;
    }
    const id = events.length ? events[events.length - 1].id + 1 : 1;
    setEvents([...events, { ...newEvent, id }]);
    setNewEvent({ name: "", date: "", venue: "", strength: 0, shortDesc: "", about: "", learning: "" });
  };

  const handleDeleteEvent = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEvents(events.filter(ev => ev.id !== id));
    }
  };

  const toggleViewDetails = (id) => {
    setExpandedEventId(expandedEventId === id ? null : id);
  };

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Admin Dashboard</h1>
      <p className="text-gray-600 mb-8">Manage events, users, and organizers here.</p>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Event Management */}
        <div className="lg:col-span-2 bg-white rounded-2xl shadow-xl p-6">
          <h2 className="text-2xl font-semibold mb-4 text-gray-700">Manage Events</h2>

          {/* New Event Form */}
          <div className="mb-6 grid grid-cols-1 sm:grid-cols-2 gap-4">
            <input type="text" placeholder="Event Name" value={newEvent.name} onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400"/>
            <input type="date" value={newEvent.date} onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400"/>
            <input type="text" placeholder="Venue" value={newEvent.venue} onChange={(e) => setNewEvent({ ...newEvent, venue: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400"/>
            <input type="number" placeholder="Total Seats" value={newEvent.strength} onChange={(e) => setNewEvent({ ...newEvent, strength: parseInt(e.target.value) })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400"/>
            <input type="text" placeholder="Short Description" value={newEvent.shortDesc} onChange={(e) => setNewEvent({ ...newEvent, shortDesc: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400 col-span-2"/>
            <input type="text" placeholder="About Event" value={newEvent.about} onChange={(e) => setNewEvent({ ...newEvent, about: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400 col-span-2"/>
            <input type="text" placeholder="Learning Insights" value={newEvent.learning} onChange={(e) => setNewEvent({ ...newEvent, learning: e.target.value })} className="border rounded-lg p-2 focus:ring-2 focus:ring-blue-400 col-span-2"/>
          </div>
          <button onClick={handleAddEvent} className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition">Add Event</button>

          {/* Event List */}
          <div className="mt-6">
            {events.map(ev => (
              <div key={ev.id} className="flex flex-col sm:flex-row justify-between items-start sm:items-center bg-gray-100 rounded-xl p-4 mb-4 shadow-sm">
                <div>
                  <h3 className="font-semibold text-lg">{ev.name}</h3>
                  <p className="text-gray-600 text-sm">Date: {ev.date} | Venue: {ev.venue} | Seats: {ev.strength}</p>
                  {expandedEventId === ev.id && (
                    <div className="mt-2 text-gray-700 border-t pt-2 space-y-1">
                      <p><span className="font-semibold">Short Description:</span> {ev.shortDesc}</p>
                      <p><span className="font-semibold">About:</span> {ev.about}</p>
                      <p><span className="font-semibold">Learning Insights:</span> {ev.learning}</p>
                      <p><span className="font-semibold">Venue:</span> {ev.venue}</p>
                      <p><span className="font-semibold">Total Seats:</span> {ev.strength}</p>
                    </div>
                  )}
                </div>
                <div className="flex gap-2 mt-2 sm:mt-0">
                  <button onClick={() => toggleViewDetails(ev.id)} className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded-lg transition">
                    {expandedEventId === ev.id ? "Hide" : "View"}
                  </button>
                  <button onClick={() => handleDeleteEvent(ev.id)} className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-lg transition">Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Users & Organizers */}
        <div className="flex flex-col gap-6">
          <div className="bg-white rounded-2xl shadow-xl p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Users</h2>
            <ul className="space-y-2">{users.map(u => (<li key={u.id} className="p-2 border rounded-lg flex justify-between items-center hover:bg-gray-50 transition">{u.name} ({u.email})</li>))}</ul>
          </div>
          <div className="bg-white rounded-2xl shadow-xl p-6">
            <h2 className="text-xl font-semibold mb-4 text-gray-700">Organizers</h2>
            <ul className="space-y-2">{organizers.map(o => (<li key={o.id} className="p-2 border rounded-lg flex justify-between items-center hover:bg-gray-50 transition">{o.name} ({o.email})</li>))}</ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
