// src/pages/UserDashboard.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { base_url } from "../components/baseUrl";

function UserDashboard() {
  const [upcomingEvents, setUpcomingEvents] = useState([]);
  const [expandedEventId, setExpandedEventId] = useState(null);
  const [registeredEvents, setRegisteredEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(null);

  // Fetch events from backend API using axios
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await axios.get(base_url+"events"); // Replace with your actual API endpoint
        setUpcomingEvents(response.data);
      } catch (error) {
        console.error("Error fetching events:", error);
      }
    };

    fetchEvents();
  }, []);

  const pastEvents = [
    { id: 4, name: "Python Bootcamp", date: "2025-08-12", image: "/assets/python.jpg" },
    { id: 5, name: "Data Science Workshop", date: "2025-07-20", image: "/assets/ds.jpg" },
  ];

  const handleRegister = (id) => {
    if (registeredEvents.includes(id)) {
      alert("You have already registered for this event!");
      return;
    }
    setUpcomingEvents(prev =>
      prev.map(event => event.id === id && event.strength > 0 ? { ...event, strength: event.strength - 1 } : event)
    );
    setRegisteredEvents(prev => [...prev, id]);
    alert("Successfully registered!");
  };

  const toggleDetails = (id) => setExpandedEventId(expandedEventId === id ? null : id);

  // Calendar generation
  const daysInMonth = Array.from({ length: 30 }, (_, i) => i + 1);
  const month = "September 2025";

  // Events filtered by selected date
  const eventsOnSelectedDate = selectedDate
    ? upcomingEvents.filter(ev => ev.date === selectedDate)
    : upcomingEvents;

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Student Dashboard</h1>
      <p className="text-gray-600 mb-8">Browse and register for events here.</p>

      <div className="flex flex-col md:flex-row gap-8">
        {/* Sidebar Calendar */}
        <aside className="md:w-1/4 bg-white rounded-2xl shadow-xl p-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-700">Event Calendar</h2>
          <p className="text-gray-500 mb-4">{month}</p>

          <div className="grid grid-cols-7 gap-1 text-center mb-2">
            {["Sun","Mon","Tue","Wed","Thu","Fri","Sat"].map(day => (
              <div key={day} className="font-semibold text-gray-600">{day}</div>
            ))}
          </div>

          <div className="grid grid-cols-7 gap-1">
            {daysInMonth.map(day => {
              const dateStr = `2025-09-${day < 10 ? "0" + day : day}`;
              const hasEvent = upcomingEvents.some(ev => ev.date === dateStr);
              const isSelected = selectedDate === dateStr;
              return (
                <div
                  key={day}
                  className={`h-10 flex items-center justify-center rounded-lg text-sm border cursor-pointer
                    ${hasEvent ? "bg-green-200 border-green-400 font-bold" : "bg-gray-50 border-gray-200"}
                    ${isSelected ? "ring-2 ring-blue-500" : ""}`}
                  title={hasEvent ? upcomingEvents.filter(ev => ev.date === dateStr).map(ev => ev.name).join(", ") : ""}
                  onClick={() => setSelectedDate(dateStr)}
                >
                  {day}
                </div>
              );
            })}
          </div>
          <p className="text-sm text-gray-500 mt-2">Green indicates upcoming events</p>
        </aside>

        {/* Events Section */}
        <div className="flex-1">
          <section className="mb-12">
            <h2 className="text-2xl font-semibold mb-4 text-gray-700">
              {selectedDate ? `Events on ${selectedDate}` : "Upcoming Events"}
            </h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              {eventsOnSelectedDate.length > 0 ? eventsOnSelectedDate.map(event => (
                <div key={event.id} className="bg-white shadow-xl rounded-2xl overflow-hidden transition-transform transform hover:scale-105 duration-300">
                  <img src={event.image || "/assets/default.jpg"} alt={event.name} className="w-full h-48 object-cover" />
                  <div className="p-6">
                    <h3 className="text-2xl font-semibold text-gray-800">{event.name}</h3>
                    <p className="text-gray-600 mt-2">{event.shortDesc}</p>
                    <p className="text-gray-500 mt-1 text-sm"><span className="font-semibold">Date:</span> {event.date}</p>

                    <div className="mt-4 flex justify-between items-center">
                      <div>
                        <p className="text-gray-700"><span className="font-semibold">üìç Venue:</span> {event.venue}</p>
                        <p className="text-gray-700"><span className="font-semibold">Seats Left:</span> {event.strength > 0 ? event.strength : "Closed"}</p>
                      </div>
                      <div className="flex gap-3">
                        <button
                          onClick={() => toggleDetails(event.id)}
                          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
                        >
                          {expandedEventId === event.id ? "Hide" : "View"}
                        </button>
                        <button
                          onClick={() => handleRegister(event.id)}
                          disabled={event.strength <= 0 || registeredEvents.includes(event.id)}
                          className={`px-4 py-2 rounded-lg ${event.strength > 0 && !registeredEvents.includes(event.id) ? "bg-green-600 hover:bg-green-700 text-white" : "bg-gray-400 text-gray-200 cursor-not-allowed"} transition`}
                        >
                          {registeredEvents.includes(event.id) ? "Registered" : event.strength > 0 ? "Register" : "Closed"}
                        </button>
                      </div>
                    </div>

                    {expandedEventId === event.id && (
                      <div className="mt-4 text-gray-700 border-t pt-3 space-y-2">
                        <p><span className="font-semibold">About:</span> {event.about}</p>
                        <p><span className="font-semibold">Venue:</span> {event.venue}</p>
                        <p><span className="font-semibold">Seats Left:</span> {event.strength > 0 ? event.strength : "Closed"}</p>
                      </div>
                    )}
                  </div>
                </div>
              )) : (
                <p className="text-gray-500">No events on this date.</p>
              )}
            </div>
          </section>

          {/* Past Events */}
          <section>
            <h2 className="text-2xl font-semibold mb-4 text-gray-700">Past Events</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {pastEvents.map(event => (
                <div key={event.id} className="bg-gray-100 rounded-xl shadow-sm overflow-hidden transform hover:scale-105 transition-transform duration-300">
                  <img src={event.image} alt={event.name} className="w-full h-40 object-cover" />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-800">{event.name}</h3>
                    <p className="text-gray-500 text-sm">{event.date}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>
      </div>
    </div>
  );
}

export default UserDashboard;
