// src/pages/OrganizerDashboard.js
import React, { useState } from "react";

function OrganizerDashboard() {
  // Sample events (replace with DB if you integrate later)
  const initialEvents = [
    { id: 1, name: "React Workshop", date: "2025-09-10", shortDesc: "Learn React basics and hooks.", about: "Deep dive into React basics and hooks, projects, and best practices.", image: "/assets/react.jpg", venue: "Hall A", strength: 10 },
    { id: 2, name: "AI Seminar", date: "2025-09-15", shortDesc: "Intro to AI concepts.", about: "Learn about AI concepts, machine learning, and real-world applications.", image: "/assets/ai.jpg", venue: "Hall B", strength: 5 },
    { id: 3, name: "Web Dev Meetup", date: "2025-09-20", shortDesc: "Networking and projects.", about: "Collaborate with peers, work on projects, and improve your web development skills.", image: "/assets/web.jpg", venue: "Hall C", strength: 8 },
  ];

  const [events, setEvents] = useState(initialEvents);
  const [expandedEventId, setExpandedEventId] = useState(null);

  const pastEvents = [
    { id: 4, name: "Python Bootcamp", date: "2025-08-12", image: "/assets/python.jpg" },
    { id: 5, name: "Data Science Workshop", date: "2025-07-20", image: "/assets/ds.jpg" },
  ];

  const deleteEvent = (id) => {
    if (window.confirm("Are you sure you want to delete this event?")) {
      setEvents(prev => prev.filter(ev => ev.id !== id));
    }
  };

  const toggleDetails = (id) => {
    setExpandedEventId(expandedEventId === id ? null : id);
  };

  // Simple calendar
  const daysInMonth = Array.from({ length: 30 }, (_, i) => i + 1);
  const month = "September 2025";

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Organizer Dashboard</h1>
      <p className="text-gray-600 mb-8">Manage and monitor events here.</p>

      <div className="flex flex-col md:flex-row gap-8">
        {/* Sidebar Calendar */}
        <aside className="md:w-1/4 bg-white rounded-2xl shadow-xl p-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-700">Event Calendar</h2>
          <p className="text-gray-500 mb-4">{month}</p>
          <div className="grid grid-cols-7 gap-1 text-center mb-2">
            {["Sun","Mon","Tue","Wed","Thu","Fri","Sat"].map(day => (
              <div key={day} className="font-semibold text-gray-600">{day}</div>
            ))}
          </div>
          <div className="grid grid-cols-7 gap-1">
            {daysInMonth.map(day => {
              const dateStr = `2025-09-${day < 10 ? "0" + day : day}`;
              const hasEvent = events.some(ev => ev.date === dateStr);
              return (
                <div
                  key={day}
                  className={`h-10 flex items-center justify-center rounded-lg text-sm border ${
                    hasEvent ? "bg-green-200 border-green-400 font-bold" : "bg-gray-50 border-gray-200"
                  }`}
                  title={hasEvent ? events.filter(ev => ev.date === dateStr).map(ev => ev.name).join(", ") : ""}
                >
                  {day}
                </div>
              );
            })}
          </div>
          <p className="text-sm text-gray-500 mt-2">Green indicates upcoming events</p>
        </aside>

        {/* Events Section */}
        <div className="flex-1">
          {/* Upcoming Events */}
          <section className="mb-12">
            <h2 className="text-2xl font-semibold mb-4 text-gray-700">Your Events</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
              {events.map(event => (
                <div key={event.id} className="bg-white shadow-xl rounded-2xl overflow-hidden transition-transform transform hover:scale-105 duration-300">
                  <img src={event.image} alt={event.name} className="w-full h-48 object-cover" />
                  <div className="p-6">
                    <h3 className="text-2xl font-semibold text-gray-800">{event.name}</h3>
                    <p className="text-gray-600 mt-2">{event.shortDesc}</p>
                    <p className="text-gray-500 mt-1 text-sm"><span className="font-semibold">Date:</span> {event.date}</p>

                    <div className="mt-4 flex justify-between items-center">
                      <div>
                        <p className="text-gray-700"><span className="font-semibold">üìç Venue:</span> {event.venue}</p>
                        <p className="text-gray-700"><span className="font-semibold">Seats Left:</span> {event.strength > 0 ? event.strength : "Closed"}</p>
                      </div>
                      <div className="flex gap-3">
                        <button
                          onClick={() => toggleDetails(event.id)}
                          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
                        >
                          {expandedEventId === event.id ? "Hide" : "View"}
                        </button>
                        <button
                          onClick={() => deleteEvent(event.id)}
                          className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition"
                        >
                          Delete
                        </button>
                      </div>
                    </div>

                    {expandedEventId === event.id && (
                      <div className="mt-4 text-gray-700 border-t pt-3 space-y-2">
                        <p><span className="font-semibold">About:</span> {event.about}</p>
                        <p><span className="font-semibold">Venue:</span> {event.venue}</p>
                        <p><span className="font-semibold">Seats Left:</span> {event.strength > 0 ? event.strength : "Closed"}</p>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Past Events */}
          <section>
            <h2 className="text-2xl font-semibold mb-4 text-gray-700">Past Events</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {pastEvents.map(event => (
                <div key={event.id} className="bg-gray-100 rounded-xl shadow-sm overflow-hidden transform hover:scale-105 transition-transform duration-300">
                  <img src={event.image} alt={event.name} className="w-full h-40 object-cover" />
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-800">{event.name}</h3>
                    <p className="text-gray-500 text-sm">{event.date}</p>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>
      </div>
    </div>
  );
}

export default OrganizerDashboard;
