import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Toast from "./Toast"; // optional toast component if you want popup

function EventCard({ image, title, date, description, venue, onRegister, isUser, seats }) {
  const [showDetails, setShowDetails] = useState(false);
  const [seatsLeft, setSeatsLeft] = useState(seats || 0);
  const [toastVisible, setToastVisible] = useState(false);
  const navigate = useNavigate();

  const handleRegister = () => {
    if (seatsLeft > 0) {
      setSeatsLeft(seatsLeft - 1);
      if (onRegister) onRegister(); // call parent function if provided
      setToastVisible(true);
      setTimeout(() => setToastVisible(false), 2000);
    } else {
      navigate("/registration-closed"); // navigate to registration closed page
    }
  };

  return (
    <div className="rounded-xl shadow-lg overflow-hidden bg-white relative">
      {/* Toast Notification */}
      {toastVisible && <Toast message="Successfully Registered!" show={toastVisible} />}

      {/* Event Image */}
      <img src={image} alt={title} className="w-full h-40 object-cover" />

      {/* Event Info */}
      <div className="p-4">
        <h3 className="font-semibold text-lg">{title}</h3>
        <p className="text-gray-600 text-sm mt-1">{date}</p>

        {/* Buttons */}
        <div className="mt-4 flex gap-2">
          <button
            onClick={() => setShowDetails(!showDetails)}
            className="flex-1 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg"
          >
            {showDetails ? "Hide" : "View"}
          </button>
          {isUser && (
            <button
              onClick={handleRegister}
              disabled={seatsLeft <= 0}
              className={`flex-1 px-4 py-2 rounded-lg ${
                seatsLeft > 0
                  ? "bg-green-500 hover:bg-green-600 text-white"
                  : "bg-gray-400 text-gray-700 cursor-not-allowed"
              }`}
            >
              {seatsLeft > 0 ? `Register (${seatsLeft})` : "Closed"}
            </button>
          )}
        </div>

        {/* Toggle Details */}
        {showDetails && (
          <div className="mt-4 text-sm text-gray-700 space-y-2 border-t pt-3">
            <p>
              <span className="font-semibold">üìç Venue:</span> {venue}
            </p>
            <p>
              <span className="font-semibold">‚ÑπÔ∏è About:</span> {description}
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default EventCard;
